static gboolean match_hostname(const char *cert_hostname, const char *hostname)
{
	const char *hostname_left;
	if (!strcasecmp(cert_hostname, hostname)) { 
		return TRUE;
	} else if (cert_hostname[0] == '*' && cert_hostname[1] == '.' && cert_hostname[2] != 0) { 
		hostname_left = strchr(hostname, '.');
		if (hostname_left != NULL && ! strcasecmp(hostname_left + 1, cert_hostname + 2)) {
			return TRUE;
		}
	}
	return FALSE;
}

static void csi_K(struct vc_data *vc, int vpar)
{
	unsigned int count;
	unsigned short *start = (unsigned short *)vc->vc_pos;
	int offset;
	switch (vpar) {
		case 0:	
			offset = 0;
			count = vc->vc_cols - vc->vc_x;
			break;
		case 1:	
			offset = -vc->vc_x;
			count = vc->vc_x + 1;
			break;
		case 2: 
			offset = -vc->vc_x;
			count = vc->vc_cols;
			break;
		default:
			return;
	}
	vc_uniscr_clear_line(vc, vc->vc_x + offset, count);
	scr_memsetw(start + offset, vc->vc_video_erase_char, 2 * count);
	vc->vc_need_wrap = 0;
	if (con_should_update(vc))
		do_update_region(vc, (unsigned long)(start + offset), count);
}

decode_definite_string(CBORDecoderObject *self, Py_ssize_t length)
{
    PyObject *ret = NULL;
    char *buf;
    buf = PyMem_Malloc(length);
    if (!buf)
        return PyErr_NoMemory();
    if (fp_read(self, buf, length) == 0)
        ret = PyUnicode_DecodeUTF8(
                buf, length, PyBytes_AS_STRING(self->str_errors));
    PyMem_Free(buf);
    if (!ret)
        return NULL;
    if (string_namespace_add(self, ret, length) == -1) {
        Py_DECREF(ret);
        return NULL;
    }
    return ret;
}

bsg_write(struct file *file, const char __user *buf, size_t count, loff_t *ppos)
{
	struct bsg_device *bd = file->private_data;
	ssize_t bytes_written;
	int ret;
	dprintk("%s: write %Zd bytes\n", bd->name, count);
	if (unlikely(segment_eq(get_fs(), KERNEL_DS)))
		return -EINVAL;
	bsg_set_block(bd, file);
	bytes_written = 0;
	ret = __bsg_write(bd, buf, count, &bytes_written,
			  file->f_mode & FMODE_WRITE);
	*ppos = bytes_written;
	if (!bytes_written || err_block_err(ret))
		bytes_written = ret;
	dprintk("%s: returning %Zd\n", bd->name, bytes_written);
	return bytes_written;
}

