int mb2_cache_entry_create(struct mb2_cache *cache, gfp_t mask, u32 key,
			   sector_t block)
{
	struct mb2_cache_entry *entry, *dup;
	struct hlist_bl_node *dup_node;
	struct hlist_bl_head *head;
	entry = kmem_cache_alloc(mb2_entry_cache, mask);
	if (!entry)
		return -ENOMEM;
	INIT_LIST_HEAD(&entry->e_lru_list);
	atomic_set(&entry->e_refcnt, 1);
	entry->e_key = key;
	entry->e_block = block;
	head = &cache->c_hash[hash_32(key, cache->c_bucket_bits)];
	entry->e_hash_list_head = head;
	hlist_bl_lock(head);
	hlist_bl_for_each_entry(dup, dup_node, head, e_hash_list) {
		if (dup->e_key == key && dup->e_block == block) {
			hlist_bl_unlock(head);
			kmem_cache_free(mb2_entry_cache, entry);
			return -EBUSY;
		}
	}
	hlist_bl_add_head(&entry->e_hash_list, head);
	hlist_bl_unlock(head);
	spin_lock(&cache->c_lru_list_lock);
	list_add_tail(&entry->e_lru_list, &cache->c_lru_list);
	atomic_inc(&entry->e_refcnt);
	cache->c_entry_count++;
	spin_unlock(&cache->c_lru_list_lock);
	return 0;
}

static bool dccp_new(struct nf_conn *ct, const struct sk_buff *skb,
		     unsigned int dataoff, unsigned int *timeouts)
{
	struct net *net = nf_ct_net(ct);
	struct dccp_net *dn;
	struct dccp_hdr _dh, *dh;
	const char *msg;
	u_int8_t state;
	dh = skb_header_pointer(skb, dataoff, sizeof(_dh), &dh);
	BUG_ON(dh == NULL);
	state = dccp_state_table[CT_DCCP_ROLE_CLIENT][dh->dccph_type][CT_DCCP_NONE];
	switch (state) {
	default:
		dn = dccp_pernet(net);
		if (dn->dccp_loose == 0) {
			msg = "nf_ct_dccp: not picking up existing connection ";
			goto out_invalid;
		}
	case CT_DCCP_REQUEST:
		break;
	case CT_DCCP_INVALID:
		msg = "nf_ct_dccp: invalid state transition ";
		goto out_invalid;
	}
	ct->proto.dccp.role[IP_CT_DIR_ORIGINAL] = CT_DCCP_ROLE_CLIENT;
	ct->proto.dccp.role[IP_CT_DIR_REPLY] = CT_DCCP_ROLE_SERVER;
	ct->proto.dccp.state = CT_DCCP_NONE;
	ct->proto.dccp.last_pkt = DCCP_PKT_REQUEST;
	ct->proto.dccp.last_dir = IP_CT_DIR_ORIGINAL;
	ct->proto.dccp.handshake_seq = 0;
	return true;
out_invalid:
	if (LOG_INVALID(net, IPPROTO_DCCP))
		nf_log_packet(net, nf_ct_l3num(ct), 0, skb, NULL, NULL,
			      NULL, "%s", msg);
	return false;
}

int    ReadDoubleArray(unsigned char *sString, int nLen, double (&pArray)[nMax])
{
    int nStart = 0;
    while( sString[nStart] != '[' )
    {
        nStart++;
        if ( nStart >= nLen )
            return 0;
    }
    int nEnd = ++nStart;
    while ( sString[nEnd] != ']' )
    {
        nEnd++;
        if ( nEnd >= nLen )
            return 0;
    }
    sString = sString + nStart;
    nLen    = nEnd - nStart;
    int nCount = 0;
    const int c_nNumLimit = 32;
    unsigned char sBuffer[c_nNumLimit];
    int nBufPos = 0;
    bool bNewItem = true;
    for ( int nIndex = 0; nIndex < nLen; ++nIndex )
    {
        unsigned char unChar = sString[nIndex];
        if ( ' ' == unChar )
        {
            if ( !bNewItem )
                bNewItem = true;
            continue;
        }
        if ( bNewItem )
        {
            if ( nCount >= nMax )
                break;
            if ( nCount > 0 )
                pArray[nCount - 1] = Utils::GetDouble( (const char *)sBuffer );
            memset( sBuffer, 0x00, c_nNumLimit );
            nBufPos = 0;
            bNewItem = false;
            nCount++;
        }
        sBuffer[nBufPos++] = unChar;
        if (nBufPos >= c_nNumLimit)
            break;
    }
    if ( 0 != sBuffer[0] && nCount > 0 )
        pArray[nCount - 1] = Utils::GetDouble( (const char *)sBuffer );
    return nCount;
}

