static struct sock *__udp4_lib_mcast_demux_lookup(struct net *net,
						  __be16 loc_port, __be32 loc_addr,
						  __be16 rmt_port, __be32 rmt_addr,
						  int dif)
{
	struct sock *sk, *result;
	struct hlist_nulls_node *node;
	unsigned short hnum = ntohs(loc_port);
	unsigned int count, slot = udp_hashfn(net, hnum, udp_table.mask);
	struct udp_hslot *hslot = &udp_table.hash[slot];
	if (hslot->count > 10)
		return NULL;
	rcu_read_lock();
begin:
	count = 0;
	result = NULL;
	sk_nulls_for_each_rcu(sk, node, &hslot->head) {
		if (__udp_is_mcast_sock(net, sk,
					loc_port, loc_addr,
					rmt_port, rmt_addr,
					dif, hnum)) {
			result = sk;
			++count;
		}
	}
	if (get_nulls_value(node) != slot)
		goto begin;
	if (result) {
		if (count != 1 ||
		    unlikely(!atomic_inc_not_zero_hint(&result->sk_refcnt, 2)))
			result = NULL;
		else if (unlikely(!__udp_is_mcast_sock(net, result,
						       loc_port, loc_addr,
						       rmt_port, rmt_addr,
						       dif, hnum))) {
			sock_put(result);
			result = NULL;
		}
	}
	rcu_read_unlock();
	return result;
}

static BOOL handle_SSH2_newkeys(PTInstVar pvar)
{
	logputs(LOG_LEVEL_VERBOSE, "SSH2_MSG_NEWKEYS was received(DH key generation is completed).");
	if (LogLevel(pvar, LOG_LEVEL_SSHDUMP)) {
		save_memdump(LOGDUMP);
	}
	finish_memdump();
	pvar->kex_status |= KEX_FLAG_NEWKEYS_RECEIVED;
	ssh2_set_newkeys(pvar, MODE_IN);
	if (!CRYPT_start_encryption(pvar, 0, 1)) {
	}
	pvar->ssh2_keys[MODE_IN].mac.enabled = 1;
	pvar->ssh2_keys[MODE_IN].comp.enabled = 1;
	enable_recv_compression(pvar);
	if (pvar->server_strict_kex) {
		pvar->ssh_state.receiver_sequence_number = 0;
	}
	SSH2_dispatch_add_message(SSH2_MSG_EXT_INFO);
	if (pvar->kex_status & KEX_FLAG_NEWKEYS_SENT) {
		if (pvar->kex_status & KEX_FLAG_REKEYING) {
			int i;
			Channel_t *c;
			do_SSH2_dispatch_setup_for_transfer(pvar);
			for (i = 0 ; i < CHANNEL_MAX ; i++) {
				c = &channels[i];
				if (c->used) {
					ssh2_channel_retry_send_bufchain(pvar, c);
				}
			}
		}
		else {
			ssh2_finish_encryption_setup(pvar);
			ssh2_prep_userauth(pvar);
		}
		pvar->kex_status = KEX_FLAG_KEXDONE;
	}
	return TRUE;
}

date_strftime_internal(int argc, VALUE *argv, VALUE self,
		       const char *default_fmt,
		       void (*func)(VALUE, struct tmx *))
{
    VALUE vfmt;
    const char *fmt;
    long len;
    char buffer[SMALLBUF], *buf = buffer;
    struct tmx tmx;
    VALUE str;
    rb_scan_args(argc, argv, "01", &vfmt);
    if (argc < 1)
	vfmt = rb_usascii_str_new2(default_fmt);
    else {
	StringValue(vfmt);
	if (!rb_enc_str_asciicompat_p(vfmt)) {
	    rb_raise(rb_eArgError,
		     "format should have ASCII compatible encoding");
	}
    }
    fmt = RSTRING_PTR(vfmt);
    len = RSTRING_LEN(vfmt);
    (*func)(self, &tmx);
    if (memchr(fmt, '\0', len)) {
	const char *p = fmt, *pe = fmt + len;
	str = rb_str_new(0, 0);
	while (p < pe) {
	    len = date_strftime_alloc(&buf, p, &tmx);
	    rb_str_cat(str, buf, len);
	    p += strlen(p);
	    if (buf != buffer) {
		xfree(buf);
		buf = buffer;
	    }
	    for (fmt = p; p < pe && !*p; ++p);
	    if (p > fmt) rb_str_cat(str, fmt, p - fmt);
	}
	rb_enc_copy(str, vfmt);
	return str;
    }
    else
	len = date_strftime_alloc(&buf, fmt, &tmx);
    str = rb_str_new(buf, len);
    if (buf != buffer) xfree(buf);
    rb_enc_copy(str, vfmt);
    return str;
}

static void _ipmi_destroy_user(struct ipmi_user *user)
{
	struct ipmi_smi  *intf = user->intf;
	int              i;
	unsigned long    flags;
	struct cmd_rcvr  *rcvr;
	struct cmd_rcvr  *rcvrs = NULL;
	if (!acquire_ipmi_user(user, &i)) {
		synchronize_srcu(&user->release_barrier);
		return;
	}
	rcu_assign_pointer(user->self, NULL);
	release_ipmi_user(user, i);
	synchronize_srcu(&user->release_barrier);
	if (user->handler->shutdown)
		user->handler->shutdown(user->handler_data);
	if (user->handler->ipmi_watchdog_pretimeout)
		smi_remove_watch(intf, IPMI_WATCH_MASK_CHECK_WATCHDOG);
	if (user->gets_events)
		atomic_dec(&intf->event_waiters);
	spin_lock_irqsave(&intf->seq_lock, flags);
	list_del_rcu(&user->link);
	for (i = 0; i < IPMI_IPMB_NUM_SEQ; i++) {
		if (intf->seq_table[i].inuse
		    && (intf->seq_table[i].recv_msg->user == user)) {
			intf->seq_table[i].inuse = 0;
			smi_remove_watch(intf, IPMI_WATCH_MASK_CHECK_MESSAGES);
			ipmi_free_recv_msg(intf->seq_table[i].recv_msg);
		}
	}
	spin_unlock_irqrestore(&intf->seq_lock, flags);
	mutex_lock(&intf->cmd_rcvrs_mutex);
	list_for_each_entry_rcu(rcvr, &intf->cmd_rcvrs, link) {
		if (rcvr->user == user) {
			list_del_rcu(&rcvr->link);
			rcvr->next = rcvrs;
			rcvrs = rcvr;
		}
	}
	mutex_unlock(&intf->cmd_rcvrs_mutex);
	synchronize_rcu();
	while (rcvrs) {
		rcvr = rcvrs;
		rcvrs = rcvr->next;
		kfree(rcvr);
	}
	kref_put(&intf->refcount, intf_free);
	module_put(intf->owner);
}

static int32_t check_load_area(uintptr_t dst, uintptr_t len)
{
	uint32_t legacy = dst + len <= UINT32_MAX - 1 ? 1 : 0;
	uintptr_t dram_start, dram_end;
	uintptr_t prot_start, prot_end;
	int32_t result = IO_SUCCESS;
	dram_start = legacy ? DRAM1_NS_BASE : DRAM_40BIT_BASE;
	dram_end = legacy ? DRAM1_NS_BASE + DRAM1_NS_SIZE :
	    DRAM_40BIT_BASE + DRAM_40BIT_SIZE;
	prot_start = legacy ? DRAM_PROTECTED_BASE : DRAM_40BIT_PROTECTED_BASE;
	prot_end = prot_start + DRAM_PROTECTED_SIZE;
	if (dst < dram_start || dst > dram_end - len || dram_end < len) {
		ERROR("BL2: dst address is on the protected area.\n");
		result = IO_FAIL;
		goto done;
	}
	if (dst >= prot_start && dst < prot_end) {
		ERROR("BL2: dst address is on the protected area.\n");
		result = IO_FAIL;
	}
	if ((dst < prot_start && dst > prot_start - len) || prot_start < len) {
		ERROR("BL2: loaded data is on the protected area.\n");
		result = IO_FAIL;
	}
done:
	if (result == IO_FAIL) {
		ERROR("BL2: Out of range : dst=0x%lx len=0x%lx\n", dst, len);
	}
	if (addr_loaded_cnt >= CHECK_IMAGE_AREA_CNT) {
		ERROR("BL2: max loadable non secure images reached\n");
		result = IO_FAIL;
	}
	addr_loaded[addr_loaded_cnt].dest = dst;
	addr_loaded[addr_loaded_cnt].length = len;
	for(int n=0; n<addr_loaded_cnt; n++) {
		if (((dst > addr_loaded[n].dest) &&
		     (dst <  addr_loaded[n].dest + addr_loaded[n].length)) ||
		    (((dst < addr_loaded[n].dest) &&
		      (dst + len)) > addr_loaded[n].dest)) {
			ERROR("BL2: image is inside a previous image area.\n");
			result = IO_FAIL;
		}
	}
	addr_loaded_cnt++;
	return result;
}

