static void write_cb(struct bt_att_chan *chan, uint8_t opcode, const void *pdu,
					uint16_t length, void *user_data)
{
	struct bt_gatt_server *server = user_data;
	struct gatt_db_attribute *attr;
	uint16_t handle = 0;
	struct async_write_op *op = NULL;
	uint8_t ecode;
	if (length < 2) {
		ecode = BT_ATT_ERROR_INVALID_PDU;
		goto error;
	}
	ecode = authorize_req(server, opcode, handle);
	if (ecode)
		goto error;
	handle = get_le16(pdu);
	attr = gatt_db_get_attribute(server->db, handle);
	if (!attr) {
		ecode = BT_ATT_ERROR_INVALID_HANDLE;
		goto error;
	}
	util_debug(server->debug_callback, server->debug_data,
				"Write %s - handle: 0x%04x",
				(opcode == BT_ATT_OP_WRITE_REQ) ? "Req" : "Cmd",
				handle);
	ecode = check_length(length, 0);
	if (ecode)
		goto error;
	ecode = check_permissions(server, attr, BT_ATT_PERM_WRITE_MASK);
	if (ecode)
		goto error;
	op = new0(struct async_write_op, 1);
	op->chan = chan;
	op->server = bt_gatt_server_ref(server);
	op->opcode = opcode;
	if (gatt_db_attribute_write(attr, 0, pdu + 2, length - 2, opcode,
							server->att,
							write_complete_cb, op))
		return;
	async_write_op_destroy(op);
	ecode = BT_ATT_ERROR_UNLIKELY;
error:
	if (opcode == BT_ATT_OP_WRITE_CMD)
		return;
	bt_att_chan_send_error_rsp(chan, opcode, handle, ecode);
}

static pj_status_t get_name(int rec_counter, const pj_uint8_t *pkt, 
                            const pj_uint8_t *start, const pj_uint8_t *max,
                            pj_str_t *name)
{
    const pj_uint8_t *p;
    pj_status_t status;
    if (rec_counter > 10) {
        return PJLIB_UTIL_EDNSINNAMEPTR;
    }
    if (start >= max)
        return PJLIB_UTIL_EDNSINNAMEPTR;
    p = start;
    while (*p) {
        if ((*p & 0xc0) == 0xc0) {
            pj_uint16_t offset;
            pj_memcpy(&offset, p, 2);
            offset ^= pj_htons((pj_uint16_t)(0xc0 << 8));
            offset = pj_ntohs(offset);
            if (offset >= max - pkt)
                return PJLIB_UTIL_EDNSINNAMEPTR;
            status = get_name(rec_counter+1, pkt, pkt + offset, max, name);
            if (status != PJ_SUCCESS)
                return status;
            return PJ_SUCCESS;
        } else {
            unsigned label_len = *p;
            if (p+1+label_len+1 > max)
                return PJLIB_UTIL_EDNSINNAMEPTR;
            pj_memcpy(name->ptr + name->slen, p+1, label_len);
            name->slen += label_len;
            p += label_len + 1;
            if (*p != 0) {
                *(name->ptr + name->slen) = '.';
                ++name->slen;
            }
        }
    }
    return PJ_SUCCESS;
}

