void Compute(OpKernelContext* context) override {
  const Tensor& tensor_in = context->input(0);
  const Tensor& grad_in = context->input(1);
  const Tensor& argmax = context->input(2);
  PoolParameters params{context,
                        ksize_,
                        stride_,
                        padding_,
                        {},
                        FORMAT_NHWC,
                        tensor_in.shape()};
  if (!context->status().ok()) {
    return;
  }
  OP_REQUIRES(context, grad_in.shape() == params.forward_output_shape(),
              errors::InvalidArgument("Expected grad shape to be ",
                                      params.forward_output_shape(),
                                      ", but got ", grad_in.shape()));
  OP_REQUIRES(context, argmax.shape() == params.forward_output_shape(),
              errors::InvalidArgument("Expected argmax shape to be ",
                                      params.forward_output_shape(),
                                      ", but got ", argmax.shape()));
  TensorShape out_shape({params.tensor_in_batch, params.tensor_in_rows,
                         params.tensor_in_cols, params.depth});
  Tensor* grad_out = nullptr;
  OP_REQUIRES_OK(context, context->forward_input_or_allocate_output(
                              {0}, 0, out_shape, &grad_out));
  if (out_shape.num_elements() == 0) return;  
  LaunchMaxPoolingGradWithArgmax<Device, T>::launch(
      context, params, grad_in, argmax, grad_out, include_batch_in_index_);
}

static void __exit pf_exit(void)
{
	struct pf_unit *pf;
	int unit;
	unregister_blkdev(major, name);
	for (pf = units, unit = 0; unit < PF_UNITS; pf++, unit++) {
		if (!pf->disk)
			continue;
		if (pf->present)
			del_gendisk(pf->disk);
		blk_cleanup_queue(pf->disk->queue);
		blk_mq_free_tag_set(&pf->tag_set);
		put_disk(pf->disk);
		if (pf->present)
			pi_release(pf->pi);
	}
}

void Wifipcap::handle_packet(WifipcapCallbacks *cbs,int header_type,
                             const struct pcap_pkthdr *header, const u_char * packet) 
{
    if (startTime == TIME_NONE) {
	startTime = header->ts;
	lastPrintTime = header->ts;
    }
    if (header->ts.tv_sec > lastPrintTime.tv_sec + Wifipcap::PRINT_TIME_INTERVAL) {
	if (verbose) {
	    int hours = (header->ts.tv_sec - startTime.tv_sec)/3600;
	    int days  = hours/24;
	    int left  = hours%24;
	    fprintf(stderr, "wifipcap: %2d days %2d hours, %10" PRId64 " pkts\n", 
		    days, left, packetsProcessed);
	}
	lastPrintTime = header->ts;
    }
    packetsProcessed++;
    WifiPacket pkt(cbs,header_type,header,packet);
    cbs->PacketBegin(pkt, packet, header->caplen, header->len);
    switch(header_type) {
    case DLT_PRISM_HEADER:
        pkt.handle_prism(packet,header->caplen);
        break;
    case DLT_IEEE802_11_RADIO:
        pkt.handle_radiotap(packet,header->caplen);
        break;
    case DLT_IEEE802_11:
        pkt.handle_80211(packet,header->caplen);
        break;
    case DLT_EN10MB:
        pkt.handle_ether(packet,header->caplen);
        break;
    default:
#if 0
        pkt.handle_ip(packet,header->caplen);
#endif
        break;
    }
    cbs->PacketEnd(pkt);
}

int pam_modutil_regain_priv(pam_handle_t *pamh, struct _ykpam_privs *privs) {
    if ((privs->saved_euid == geteuid()) && (privs->saved_egid == getegid())) {
        D (privs->debug_file, "Privilges already as requested, pretend it is all right");
        return 0;
    }
    if (seteuid(privs->saved_euid) < 0) {
        D (privs->debug_file, "seteuid: %s", strerror(errno));
        return -1;
    }
    if (setegid(privs->saved_egid) < 0) {
        D (privs->debug_file, "setegid: %s", strerror(errno));
        return -1;
    }
    if (setgroups(privs->saved_groups_length, privs->saved_groups) < 0) {
        D (privs->debug_file, "setgroups: %s", strerror(errno));
        return -1;
    }
    return 0;
}

static int openssl_push_check_result(lua_State *L, int ret, const char* name)
{
  switch (ret)
  {
  case 1:
    lua_pushboolean(L, 1);
    if (name)
    {
      lua_pushstring(L, name);
      ret = 2;
    }
    break;
  case 0:
    lua_pushboolean(L, 0);
    ret = 1;
    break;
  case -1:
    lua_pushnil(L);
    lua_pushliteral(L, "internal");
    ret = 2;
  case -2:
    lua_pushnil(L);
    lua_pushliteral(L, "malformed");
    ret = 2;
  default:
    lua_pushnil(L);
    lua_pushinteger(L, ret);
    ret = 2;
  }
  return ret;
}

