    if (virStoragePoolGetXMLDescEnsureACL(pool->conn, def) < 0)
        goto cleanup;
    if ((flags & VIR_STORAGE_XML_INACTIVE) && newDef)
        curDef = newDef;
    else
        curDef = def;
    ret = virStoragePoolDefFormat(curDef);
 cleanup:
    virStoragePoolObjEndAPI(&obj);
    return ret;
}

