static void __sock_release(struct socket *sock, struct inode *inode)
{
	if (sock->ops) {
		struct module *owner = sock->ops->owner;
		if (inode)
			inode_lock(inode);
		sock->ops->release(sock);
		if (inode)
			inode_unlock(inode);
		sock->ops = NULL;
		module_put(owner);
	}
	if (rcu_dereference_protected(sock->wq, 1)->fasync_list)
		pr_err("%s: fasync list not empty!\n", __func__);
	if (!sock->file) {
		iput(SOCK_INODE(sock));
		return;
	}
	sock->file = NULL;
}

void ComputeAsyncImpl(OpKernelContext* c, CollectiveExecutor* col_exec,
                      DoneCallback done) override {
  auto output_shape = c->input(0).shape();
  OP_REQUIRES_ASYNC(c, output_shape.dims() > 0,
                    errors::InvalidArgument("input should have rank > 0, ",
                                            "recieved ", output_shape.dims()),
                    done);
  output_shape.set_dim(
      0, output_shape.dim_size(0) * col_params_->group.group_size);
  col_params_->instance.shape = output_shape;
  if (c->mutable_output(0) == nullptr) {
    Tensor* output = nullptr;
    OP_REQUIRES_OK_ASYNC(
        c, c->allocate_output(0, col_params_->instance.shape, &output), done);
  }
  if (!CanProceedWithCompute(c, col_exec, done)) return;
  auto actual_done = [c, col_params = col_params_, done](const Status& s) {
    VLOG(1) << "CollectiveGatherOpKernel ExecuteAsync done for collective "
            << c->op_kernel().name() << " device " << c->device()->name()
            << " group " << col_params->group.group_key << " instance "
            << col_params->instance.instance_key << " status " << s;
    col_params->Unref();
    OP_REQUIRES_OK_ASYNC(c, s, done);
    done();
  };
  VLOG(1) << "CollectiveGatherOpKernel ExecuteAsync start for collective "
          << col_params_->name << " device " << c->device()->name()
          << " group " << col_params_->group.group_key << " instance "
          << col_params_->instance.instance_key;
  col_params_->Ref();
  col_exec->ExecuteAsync(c, col_params_, GetCollectiveKey(c), actual_done);
}

x509_verify_ctx_add_chain(struct x509_verify_ctx *ctx,
    struct x509_verify_chain *chain, char *name)
{
	size_t depth;
	X509 *last = x509_verify_chain_last(chain);
	X509 *leaf = x509_verify_chain_leaf(chain);
	depth = sk_X509_num(chain->certs);
	if (depth > 0)
		depth--;
	if (ctx->chains_count >= ctx->max_chains)
		return x509_verify_cert_error(ctx, last, depth,
		    X509_V_ERR_CERT_CHAIN_TOO_LONG, 0);
	if (chain->cert_errors[depth] ==
	    X509_V_ERR_UNABLE_TO_GET_ISSUER_CERT_LOCALLY)
		chain->cert_errors[depth] = X509_V_OK;
	if (!x509_verify_ctx_validate_legacy_chain(ctx, chain, depth))
		return 0;
	if ((ctx->chains[ctx->chains_count] = x509_verify_chain_dup(chain)) ==
	    NULL) {
		return x509_verify_cert_error(ctx, last, depth,
		    X509_V_ERR_OUT_OF_MEM, 0);
	}
	if (!x509_verify_cert_valid(ctx, leaf, NULL))
		return 0;
	if (!x509_verify_cert_hostname(ctx, leaf, name))
		return 0;
	ctx->chains_count++;
	ctx->error = X509_V_OK;
	ctx->error_depth = depth;
	return 1;
}

explicit DataFormatDimMapOp(OpKernelConstruction* context)
    : OpKernel(context) {
  string src_format;
  OP_REQUIRES_OK(context, context->GetAttr("src_format", &src_format));
  string dst_format;
  OP_REQUIRES_OK(context, context->GetAttr("dst_format", &dst_format));
  OP_REQUIRES(context, src_format.size() == 4 || src_format.size() == 5,
              errors::InvalidArgument(strings::StrCat(
                  "Source format must of length 4 or 5, received "
                  "src_format = ",
                  src_format)));
  OP_REQUIRES(
      context, dst_format.size() == 4 || dst_format.size() == 5,
      errors::InvalidArgument(strings::StrCat(
          "Destination format must of length 4 or 5, received dst_format = ",
          dst_format)));
  dst_idx_ = Tensor(DT_INT32, {static_cast<int64>(src_format.size())});
  for (int i = 0; i < src_format.size(); ++i) {
    for (int j = 0; j < dst_format.size(); ++j) {
      if (dst_format[j] == src_format[i]) {
        dst_idx_.vec<int>()(i) = j;
        break;
      }
    }
  }
}

void arch_pick_mmap_layout(struct mm_struct *mm)
{
	unsigned long random_factor = 0UL;
	if (current->flags & PF_RANDOMIZE)
		random_factor = arch_mmap_rnd();
	mm->mmap_legacy_base = mmap_legacy_base(random_factor);
	if (mmap_is_legacy()) {
		mm->mmap_base = mm->mmap_legacy_base;
		mm->get_unmapped_area = arch_get_unmapped_area;
	} else {
		mm->mmap_base = mmap_base(random_factor);
		mm->get_unmapped_area = arch_get_unmapped_area_topdown;
	}
}

dissect_ac_if_hdr_body(tvbuff_t *tvb, gint offset, packet_info *pinfo _U_,
        proto_tree *tree, usb_conv_info_t *usb_conv_info)
{
    gint     offset_start;
    guint16  bcdADC;
    guint8   ver_major;
    double   ver;
    guint8   if_in_collection, i;
    audio_conv_info_t *audio_conv_info;
    offset_start = offset;
    bcdADC = tvb_get_letohs(tvb, offset);
    ver_major = USB_AUDIO_BCD44_TO_DEC(bcdADC>>8);
    ver = ver_major + USB_AUDIO_BCD44_TO_DEC(bcdADC&0xFF) / 100.0;
    proto_tree_add_double_format_value(tree, hf_ac_if_hdr_ver,
            tvb, offset, 2, ver, "%2.2f", ver);
    audio_conv_info = (audio_conv_info_t *)usb_conv_info->class_data;
    if(!audio_conv_info) {
        audio_conv_info = wmem_new(wmem_file_scope(), audio_conv_info_t);
        usb_conv_info->class_data = audio_conv_info;
        usb_conv_info->class_data_type = USB_CONV_AUDIO;
    } else if (usb_conv_info->class_data_type != USB_CONV_AUDIO) {
        return 0;
    }
    audio_conv_info->ver_major = ver_major;
    offset += 2;
    if (ver_major==1) {
        proto_tree_add_item(tree, hf_ac_if_hdr_total_len,
                tvb, offset, 2, ENC_LITTLE_ENDIAN);
        offset += 2;
        if_in_collection = tvb_get_guint8(tvb, offset);
        proto_tree_add_item(tree, hf_ac_if_hdr_bInCollection,
                tvb, offset, 1, ENC_LITTLE_ENDIAN);
        offset++;
        for (i=0; i<if_in_collection; i++) {
            proto_tree_add_item(tree, hf_ac_if_hdr_if_num,
                    tvb, offset, 1, ENC_LITTLE_ENDIAN);
            offset++;
        }
    }
    return offset-offset_start;
}

SslSocket::create(Envoy::Ssl::ContextSharedPtr ctx, InitialState state,
                  const Network::TransportSocketOptionsConstSharedPtr& transport_socket_options,
                  Ssl::HandshakerFactoryCb handshaker_factory_cb) {
