crm_parse_remote_buffer(char **msg_buf)
{
    char *buf = NULL;
    char *start = NULL;
    char *end = NULL;
    xmlNode *xml = NULL;
    if (*msg_buf == NULL) {
        return NULL;
    }
    buf = *msg_buf;
    *msg_buf = NULL;
    start = buf;
    end = strstr(start, REMOTE_MSG_TERMINATOR);
    while (!xml && end) {
        end[0] = '\0';
        end += strlen(REMOTE_MSG_TERMINATOR);
        xml = string2xml(start);
        if (xml == NULL) {
            crm_err("Couldn't parse: '%.120s'", start);
        }
        start = end;
        end = strstr(start, REMOTE_MSG_TERMINATOR);
    }
    if (xml && start) {
        *msg_buf = strdup(start);
        free(buf);
    } else if (!xml) {
        *msg_buf = buf;
    }
    return xml;
}

