const FunctionDef* FunctionLibraryDefinition::GetAttrImpl(
    const NodeDef& ndef) const {
  if (ndef.op() != kGradientOp) {
    return Find(ndef.op());
  }
  const NameAttrList* forward_func_attrs;
  if (!TryGetNodeAttr(ndef, kFuncAttr, &forward_func_attrs)) {
    return nullptr;
  }
  const string& func_name = forward_func_attrs->name();
  {
    tf_shared_lock l(mu_);
    const string& grad_name = FindGradientHelper(func_name);
    if (!grad_name.empty()) {
      if (const auto helper = FindHelper(grad_name)) {
        return &(helper->fdef);
      } else {
        return nullptr;
      }
    }
    if (const auto helper = FindHelper(func_name)) {
      return &(helper->fdef);
    } else {
      return nullptr;
    }
  }
}

static int do_session_handshake (lua_State *L, int status, lua_KContext ctx) {
    int rc;
    struct ssh_userdata *sshu = NULL;
    assert(lua_gettop(L) == 4);
    sshu = (struct ssh_userdata *) nseU_checkudata(L, 3, SSH2_UDATA, "ssh2");
    while ((rc = libssh2_session_handshake(sshu->session, sshu->sp[0])) == LIBSSH2_ERROR_EAGAIN) {
        luaL_getmetafield(L, 3, "filter");
        lua_pushvalue(L, 3);
        assert(lua_status(L) == LUA_OK);
        lua_callk(L, 1, 0, 0, do_session_handshake);
    }
    if (rc) {
        libssh2_session_free(sshu->session);
        sshu->session = NULL;
        return luaL_error(L, "Unable to complete libssh2 handshake.");
    }
    lua_settop(L, 3);
    return 1;
}

char *suhosin_encrypt_single_cookie(char *name, int name_len, char *value, int value_len, char *key TSRMLS_DC)
{
	char buffer[4096];
    char buffer2[4096];
	char *buf = buffer, *buf2 = buffer2, *d, *d_url;
    int l;
	if (name_len > sizeof(buffer)-2) {
		buf = estrndup(name, name_len);
	} else {
		memcpy(buf, name, name_len);
		buf[name_len] = 0;
	}
	name_len = php_url_decode(buf, name_len);
    normalize_varname(buf);
    name_len = strlen(buf);
	if (SUHOSIN_G(cookie_plainlist)) {
		if (zend_hash_exists(SUHOSIN_G(cookie_plainlist), buf, name_len+1)) {
encrypt_return_plain:
			if (buf != buffer) {
				efree(buf);
			}
			return estrndup(value, value_len);
		}
	} else if (SUHOSIN_G(cookie_cryptlist)) {
		if (!zend_hash_exists(SUHOSIN_G(cookie_cryptlist), buf, name_len+1)) {
			goto encrypt_return_plain;
		}
	}
	if (strlen(value) <= sizeof(buffer2)-2) {
		memcpy(buf2, value, value_len);
		buf2[value_len] = 0;
	} else {
		buf2 = estrndup(value, value_len);
	}
	value_len = php_url_decode(buf2, value_len);
	d = suhosin_encrypt_string(buf2, value_len, buf, name_len, key TSRMLS_CC);
	d_url = php_url_encode(d, strlen(d), &l);
	efree(d);
    if (buf != buffer) {
		efree(buf);
	}
    if (buf2 != buffer2) {
		efree(buf2);
	}
	return d_url;
}

static int do_io_accounting(struct task_struct *task, char *buffer, int whole)
{
	struct task_io_accounting acct = task->ioac;
	unsigned long flags;
	if (!ptrace_may_access(task, PTRACE_MODE_READ))
		return -EACCES;
	if (whole && lock_task_sighand(task, &flags)) {
		struct task_struct *t = task;
		task_io_accounting_add(&acct, &task->signal->ioac);
		while_each_thread(task, t)
			task_io_accounting_add(&acct, &t->ioac);
		unlock_task_sighand(task, &flags);
	}
	return sprintf(buffer,
			"rchar: %llu\n"
			"wchar: %llu\n"
			"syscr: %llu\n"
			"syscw: %llu\n"
			"read_bytes: %llu\n"
			"write_bytes: %llu\n"
			"cancelled_write_bytes: %llu\n",
			(unsigned long long)acct.rchar,
			(unsigned long long)acct.wchar,
			(unsigned long long)acct.syscr,
			(unsigned long long)acct.syscw,
			(unsigned long long)acct.read_bytes,
			(unsigned long long)acct.write_bytes,
			(unsigned long long)acct.cancelled_write_bytes);
}

void Compute(OpKernelContext* context) final {
  const Tensor& superdiag = context->input(0);
  const Tensor& maindiag = context->input(1);
  const Tensor& subdiag = context->input(2);
  const Tensor& rhs = context->input(3);
  const int ndims = rhs.dims();
  OP_REQUIRES(
      context, ndims >= 2,
      errors::InvalidArgument("Input must have rank >= 2, but got ", ndims));
  OP_REQUIRES_OK(context, ValidateInputTensor(superdiag, "superdiag", rhs));
  OP_REQUIRES_OK(context, ValidateInputTensor(maindiag, "maindiag", rhs));
  OP_REQUIRES_OK(context, ValidateInputTensor(subdiag, "subdiag", rhs));
  int64 batch_size = 1;
  for (int i = 0; i < ndims - 2; i++) {
    batch_size *= rhs.dim_size(i);
  }
  const int m = rhs.dim_size(ndims - 2);
  const int n = rhs.dim_size(ndims - 1);
  Tensor* output;
  OP_REQUIRES_OK(context, context->allocate_output(0, rhs.shape(), &output));
  const Eigen::GpuDevice& device = context->eigen_device<Eigen::GpuDevice>();
  GpuLaunchConfig cfg = GetGpuLaunchConfig(1, device);
  TF_CHECK_OK(GpuLaunchKernel(
      TridiagonalMatMulKernel<Scalar>, cfg.block_count, cfg.thread_per_block,
      0, device.stream(), batch_size, m, n, superdiag.flat<Scalar>().data(),
      maindiag.flat<Scalar>().data(), subdiag.flat<Scalar>().data(),
      rhs.flat<Scalar>().data(), output->flat<Scalar>().data()));
}

int Dispatcher::getparam( size_t N, int defaultval )
{
  int ret = defaultval;
  if ( !parsed ) {
    parse_params();
