u64 secure_dccp_sequence_number(__be32 saddr, __be32 daddr,
				__be16 sport, __be16 dport)
{
	u32 hash[MD5_DIGEST_WORDS];
	u64 seq;
	hash[0] = (__force u32)saddr;
	hash[1] = (__force u32)daddr;
	hash[2] = ((__force u16)sport << 16) + (__force u16)dport;
	hash[3] = net_secret[15];
	md5_transform(hash, net_secret);
	seq = hash[0] | (((u64)hash[1]) << 32);
	seq += ktime_to_ns(ktime_get_real());
	seq &= (1ull << 48) - 1;
	return seq;
}

fu_plugin_set_secure_config_value(FuPlugin *self,
				  const gchar *key,
				  const gchar *value,
				  GError **error)
{
	g_autofree gchar *conf_path = fu_plugin_get_config_filename(self);
	gint ret;
	g_return_val_if_fail(FU_IS_PLUGIN(self), FALSE);
	g_return_val_if_fail(error == NULL || *error == NULL, FALSE);
	if (!g_file_test(conf_path, G_FILE_TEST_EXISTS)) {
		g_set_error(error, FWUPD_ERROR, FWUPD_ERROR_NOT_FOUND, "%s is missing", conf_path);
		return FALSE;
	}
	ret = g_chmod(conf_path, 0660);
	if (ret == -1) {
		g_set_error(error,
			    FWUPD_ERROR,
			    FWUPD_ERROR_INTERNAL,
			    "failed to set permissions on %s",
			    conf_path);
		return FALSE;
	}
	return fu_plugin_set_config_value(self, key, value, error);
}

int Bind(const Node& node, int max_retry) override {
  receiver_ = zmq_socket(context_, ZMQ_ROUTER);
  CHECK(receiver_ != NULL)
      << "create receiver socket failed: " << zmq_strerror(errno);
  int local = GetEnv("DMLC_LOCAL", 0);
  std::string addr = local ? "ipc:
  int port = node.port;
  unsigned seed = static_cast<unsigned>(time(NULL)+port);
  for (int i = 0; i < max_retry+1; ++i) {
    auto address = addr + std::to_string(port);
    if (zmq_bind(receiver_, address.c_str()) == 0) break;
    if (i == max_retry) {
      port = -1;
    } else {
      port = 10000 + rand_r(&seed) % 40000;
    }
  }
  return port;
}

static int xar_get_numeric_from_xml_element(xmlTextReaderPtr reader, long * value)
{
    const xmlChar * numstr;
    if (xmlTextReaderRead(reader) == 1 && xmlTextReaderNodeType(reader) == XML_READER_TYPE_TEXT) {
        numstr = xmlTextReaderConstValue(reader);
        if (numstr) {
            *value = atol((const char *)numstr);
            if (*value < 0) {
                cli_dbgmsg("cli_scanxar: XML element value %li\n", *value);
                return CL_EFORMAT;
            }
            return CL_SUCCESS;
        }
    }
    cli_dbgmsg("cli_scanxar: No text for XML element\n");
    return CL_EFORMAT;
}

ttwu_do_wakeup(struct rq *rq, struct task_struct *p, int wake_flags)
{
	check_preempt_curr(rq, p, wake_flags);
	p->state = TASK_RUNNING;
	trace_sched_wakeup(p);
#ifdef CONFIG_SMP
	if (p->sched_class->task_woken) {
		lockdep_unpin_lock(&rq->lock);
		p->sched_class->task_woken(rq, p);
		lockdep_pin_lock(&rq->lock);
	}
	if (rq->idle_stamp) {
		u64 delta = rq_clock(rq) - rq->idle_stamp;
		u64 max = 2*rq->max_idle_balance_cost;
		update_avg(&rq->avg_idle, delta);
		if (rq->avg_idle > max)
			rq->avg_idle = max;
		rq->idle_stamp = 0;
	}
#endif
}

crm_recv_remote_ready(void *session, gboolean encrypted, int timeout )
{
    struct pollfd fds = { 0, };
    int sock = 0;
    void *sock_ptr = NULL;
    int rc = 0;
    time_t start;
    if (encrypted) {
#ifdef HAVE_GNUTLS_GNUTLS_H
        gnutls_session *tls_session = session;
        sock_ptr = gnutls_transport_get_ptr(*tls_session);
#else
        CRM_ASSERT(encrypted == FALSE);
#endif
    } else {
        sock_ptr = session;
    }
    sock = GPOINTER_TO_INT(sock_ptr);
    if (sock <= 0) {
        return -ENOTCONN;
    }
    start = time(NULL);
    errno = 0;
    do {
        fds.fd = sock;
        fds.events = POLLIN;
        if (errno == EINTR && (timeout > 0)) {
            timeout = timeout - ((time(NULL) - start) * 1000);
            if (timeout < 1000) {
                timeout = 1000;
            }
        }
        rc = poll(&fds, 1, timeout);
    } while (rc < 0 && errno == EINTR);
    return rc;
}

ikev2_sub_print(netdissect_options *ndo,
		struct isakmp *base,
		u_char np, const struct isakmp_gen *ext, const u_char *ep,
		uint32_t phase, uint32_t doi, uint32_t proto, int depth)
{
	const u_char *cp;
	int i;
	struct isakmp_gen e;
	cp = (const u_char *)ext;
	while (np) {
		ND_TCHECK(*ext);
		UNALIGNED_MEMCPY(&e, ext, sizeof(e));
		ND_TCHECK2(*ext, ntohs(e.len));
		depth++;
		ND_PRINT((ndo,"\n"));
		for (i = 0; i < depth; i++)
			ND_PRINT((ndo,"    "));
		ND_PRINT((ndo,"("));
		cp = ikev2_sub0_print(ndo, base, np,
				      ext, ep, phase, doi, proto, depth);
		ND_PRINT((ndo,")"));
		depth--;
		if (cp == NULL) {
			return NULL;
		}
		np = e.np;
		ext = (const struct isakmp_gen *)cp;
	}
	return cp;
trunc:
	ND_PRINT((ndo," [|%s]", NPSTR(np)));
	return NULL;
}

resolve_op_from_commit (FlatpakTransaction *self,
                        FlatpakTransactionOperation *op,
                        const char *checksum,
                        GFile *sideload_path,
                        GVariant *commit_data,
                        GError **error)
{
  g_autoptr(GBytes) metadata_bytes = NULL;
  g_autoptr(GVariant) commit_metadata = NULL;
  const char *xa_metadata = NULL;
  guint64 download_size = 0;
  guint64 installed_size = 0;
  commit_metadata = g_variant_get_child_value (commit_data, 0);
  g_variant_lookup (commit_metadata, "xa.metadata", "&s", &xa_metadata);
  if (xa_metadata == NULL)
    return flatpak_fail_error (error, FLATPAK_ERROR_INVALID_DATA,
                               "No xa.metadata in local commit %s ref %s",
                               checksum, flatpak_decomposed_get_ref (op->ref));
  metadata_bytes = g_bytes_new (xa_metadata, strlen (xa_metadata));
  if (g_variant_lookup (commit_metadata, "xa.download-size", "t", &download_size))
    op->download_size = GUINT64_FROM_BE (download_size);
  if (g_variant_lookup (commit_metadata, "xa.installed-size", "t", &installed_size))
    op->installed_size = GUINT64_FROM_BE (installed_size);
  g_variant_lookup (commit_metadata, OSTREE_COMMIT_META_KEY_ENDOFLIFE, "s", &op->eol);
  g_variant_lookup (commit_metadata, OSTREE_COMMIT_META_KEY_ENDOFLIFE_REBASE, "s", &op->eol_rebase);
  return resolve_op_end (self, op, checksum, sideload_path, metadata_bytes, error);
}

static void launch(OpKernelContext* context, bool cudnn_use_autotune,
                   const Tensor& input, const Tensor& filter,
                   const std::array<int64, 3>& dilations,
                   const std::array<int64, 3>& strides, const Padding padding,
                   TensorFormat data_format, Tensor* output) {
  OP_REQUIRES(context, data_format == FORMAT_NHWC,
              errors::InvalidArgument("CPU implementation of Conv3D "
                                      "currently only supports the NHWC "
                                      "tensor format."));
  OP_REQUIRES(context,
              dilations[0] == 1 && dilations[1] == 1 && dilations[2] == 1,
              errors::InvalidArgument("CPU implementation of Conv3D "
                                      "currently only supports dilated rates "
                                      "of 1."));
  OP_REQUIRES(context, filter.dim_size(3) == input.dim_size(input.dims() - 1),
              errors::InvalidArgument(
                  "Number of channels in filter (", filter.dim_size(3),
                  ") must match last dimension of input (",
                  input.dim_size(input.dims() - 1), ")"));
  functor::CuboidConvolution<CPUDevice, T>()(
      context->eigen_device<CPUDevice>(), output->tensor<T, 5>(),
      input.tensor<T, 5>(), filter.tensor<T, 5>(), strides[2], strides[1],
      strides[0], BrainPadding2EigenPadding(padding));
}

find_method_in_class (MonoClass *klass, const char *name, const char *qname, const char *fqname,
		      MonoMethodSignature *sig, MonoClass *from_class)
{
 	int i;
	if (klass->type_token && !klass->image->dynamic && !klass->methods && !klass->rank && klass == from_class && !from_class->generic_class) {
		for (i = 0; i < klass->method.count; ++i) {
			guint32 cols [MONO_METHOD_SIZE];
			MonoMethod *method;
			const char *m_name;
			MonoMethodSignature *other_sig;
			mono_metadata_decode_table_row (klass->image, MONO_TABLE_METHOD, klass->method.first + i, cols, MONO_METHOD_SIZE);
			m_name = mono_metadata_string_heap (klass->image, cols [MONO_METHOD_NAME]);
			if (!((fqname && !strcmp (m_name, fqname)) ||
				  (qname && !strcmp (m_name, qname)) ||
				  (name && !strcmp (m_name, name))))
				continue;
			method = mono_get_method (klass->image, MONO_TOKEN_METHOD_DEF | (klass->method.first + i + 1), klass);
			other_sig = mono_method_signature (method);
			if (method && other_sig && (sig->call_convention != MONO_CALL_VARARG) && mono_metadata_signature_equal (sig, other_sig))
				return method;
		}
	}
	mono_class_setup_methods (klass);
	if (!klass->methods)
		return NULL;
	for (i = 0; i < klass->method.count; ++i) {
		MonoMethod *m = klass->methods [i];
		MonoMethodSignature *msig;
		if (!((fqname && !strcmp (m->name, fqname)) ||
		      (qname && !strcmp (m->name, qname)) ||
		      (name && !strcmp (m->name, name))))
			continue;
		msig = mono_method_signature (m);
		if (!msig)
			continue;
		if (sig->call_convention == MONO_CALL_VARARG) {
			if (mono_metadata_signature_vararg_match (sig, msig))
				break;
		} else {
			if (mono_metadata_signature_equal (sig, msig))
				break;
		}
	}
	if (i < klass->method.count)
		return mono_class_get_method_by_index (from_class, i);
	return NULL;
}

void Compute(OpKernelContext* context) override {
  const Tensor& input = context->input(0);
  const Tensor& filter = context->input(1);
  OP_REQUIRES(context, input.dims() == 5,
              errors::InvalidArgument("input must be 5-dimensional"));
  OP_REQUIRES(context, filter.dims() == 5,
              errors::InvalidArgument("filter must be 5-dimensional"));
  const int64 in_depth = GetTensorDim(input, data_format_, 'C');
  const int64 in_batch = GetTensorDim(input, data_format_, 'N');
  const int64 filter_depth = filter.dim_size(3);
  const int64 out_depth = filter.dim_size(4);
  OP_REQUIRES(context, filter_depth != 0,
              errors::InvalidArgument("filter_depth must be non-zero"));
  OP_REQUIRES(context, in_depth % filter_depth == 0,
              errors::InvalidArgument(
                  "Input depth must be evenly divisible by filter depth: ",
                  in_depth, " vs ", filter_depth));
  std::array<int64, 3> input_size = {
      {GetTensorDim(input, data_format_, '0'),
       GetTensorDim(input, data_format_, '1'),
       GetTensorDim(input, data_format_, '2')}};
  std::array<int64, 3> filter_size = {
      {filter.dim_size(0), filter.dim_size(1), filter.dim_size(2)}};
  std::array<int64, 3> dilations = {
      {GetTensorDim(dilation_, data_format_, '0'),
       GetTensorDim(dilation_, data_format_, '1'),
       GetTensorDim(dilation_, data_format_, '2')}};
  std::array<int64, 3> strides = {{GetTensorDim(stride_, data_format_, '0'),
                                   GetTensorDim(stride_, data_format_, '1'),
                                   GetTensorDim(stride_, data_format_, '2')}};
  std::array<int64, 3> out, padding;
  OP_REQUIRES_OK(
      context, Get3dOutputSizeV2(input_size, filter_size, dilations, strides,
                                 padding_, &out, &padding));
  TensorShape out_shape = ShapeFromFormat(
      data_format_, in_batch, {{out[0], out[1], out[2]}}, out_depth);
  Tensor* output;
  OP_REQUIRES_OK(context, context->allocate_output(0, out_shape, &output));
  if (out_shape.num_elements() == 0) return;
  LaunchConvOp<Device, T>::launch(context, cudnn_use_autotune_, input, filter,
                                  dilations, strides, padding_, data_format_,
                                  output);
}

