{
	u32 seq = ctx->cached_cq_tail - atomic_read(&ctx->cq_timeouts);
	spin_lock_irq(&ctx->timeout_lock);
	while (!list_empty(&ctx->timeout_list)) {
		u32 events_needed, events_got;
		struct io_kiocb *req = list_first_entry(&ctx->timeout_list,
						struct io_kiocb, timeout.list);
		if (io_is_timeout_noseq(req))
			break;
		events_needed = req->timeout.target_seq - ctx->cq_last_tm_flush;
		events_got = seq - ctx->cq_last_tm_flush;
		if (events_got < events_needed)
			break;
		list_del_init(&req->timeout.list);
		io_kill_timeout(req, 0);
	}
	ctx->cq_last_tm_flush = seq;
	spin_unlock_irq(&ctx->timeout_lock);
}

static bool map_info2_flags_to_sbuf(const SMB_STRUCT_STAT *psbuf,
				const uint32_t smb_fflags,
				const uint32_t smb_fmask,
				int *stat_fflags)
{
	uint32_t max_fmask = 0;
	int i;
	*stat_fflags = psbuf->st_ex_flags;
	for (i = 0; i < ARRAY_SIZE(info2_flags_map); ++i) {
	    max_fmask |= info2_flags_map[i].smb_fflag;
	    if (smb_fmask & info2_flags_map[i].smb_fflag) {
		    if (smb_fflags & info2_flags_map[i].smb_fflag) {
			    *stat_fflags |= info2_flags_map[i].stat_fflag;
		    } else {
			    *stat_fflags &= ~info2_flags_map[i].stat_fflag;
		    }
	    }
	}
	if ((smb_fmask & max_fmask) != smb_fmask) {
		return False;
	}
	return True;
}

void next_character(void)
{
    if (!recovery_started) {
        recovery_abort();
        fsm_sendFailure(FailureType_Failure_UnexpectedMessage, "Not in Recovery mode");
        layoutHome();
        return;
    }
    strlcpy(cipher, english_alphabet, ENGLISH_ALPHABET_BUF);
    random_permute_char(cipher, strlen(cipher));
    static char CONFIDENTIAL current_word[CURRENT_WORD_BUF];
    get_current_word(current_word);
    if (strlen(current_word) > 4) {
        memzero(current_word, sizeof(current_word));
        recovery_abort();
        fsm_sendFailure(FailureType_Failure_SyntaxError,
                        "Words were not entered correctly. Make sure you are using the substition cipher.");
        layoutHome();
        return;
    }
    CharacterRequest resp;
    memset(&resp, 0, sizeof(CharacterRequest));
    resp.word_pos = get_current_word_pos();
    resp.character_pos = strlen(current_word);
    msg_write(MessageType_MessageType_CharacterRequest, &resp);
    bool auto_completed = false;
    if (strlen(current_word) >= 3) {
        auto_completed = attempt_auto_complete(current_word);
    }
#if DEBUG_LINK
    if (auto_completed) {
        strlcpy(auto_completed_word, current_word, CURRENT_WORD_BUF);
    } else {
        auto_completed_word[0] = '\0';
    }
#endif
    format_current_word(current_word, auto_completed);
    layout_cipher(current_word, cipher);
    memzero(current_word, sizeof(current_word));
}

int unit_name_path_escape(const char *f, char **ret) {
        char *p, *s;
        assert(f);
        assert(ret);
        p = strdupa(f);
        if (!p)
                return -ENOMEM;
        path_simplify(p);
        if (empty_or_root(p))
                s = strdup("-");
        else {
                if (!path_is_normalized(p))
                        return -EINVAL;
                delete_trailing_chars(p, "/");
                p = skip_leading_chars(p, "/");
                s = unit_name_escape(p);
        }
        if (!s)
                return -ENOMEM;
        *ret = s;
        return 0;
}

static char *__filterQuotedShell(const char *arg) {
	r_return_val_if_fail (arg, NULL);
	char *a = malloc (strlen (arg) + 1);
	if (!a) {
		return NULL;
	}
	char *b = a;
	while (*arg) {
		switch (*arg) {
		case ' ':
		case '=':
		case '"':
		case '\\':
		case '\r':
		case '\n':
			break;
		default:
			*b++ = *arg;
			break;
		}
		arg++;
	}
	*b = 0;
	return a;
}

static gg_action_t gg_handle_connecting(struct gg_session *sess, struct gg_event *e, enum gg_state_t next_state, enum gg_state_t alt_state, enum gg_state_t alt2_state)
{
	int res;
