int http_parse_cont_len_header(struct ist *value, unsigned long long *body_len,
                               int not_first)
{
	char *e, *n;
	unsigned long long cl;
	struct ist word;
	int check_prev = not_first;
	word.ptr = value->ptr - 1; 
	e = value->ptr + value->len;
	while (++word.ptr < e) {
		if (unlikely(HTTP_IS_LWS(*word.ptr)))
			continue;
		for (cl = 0, n = word.ptr; n < e; n++) {
			unsigned int c = *n - '0';
			if (unlikely(c > 9)) {
				if (unlikely(n == word.ptr)) 
					goto fail;
				break;
			}
			if (unlikely(cl > ULLONG_MAX / 10ULL))
				goto fail; 
			cl = cl * 10ULL;
			if (unlikely(cl + c < cl))
				goto fail; 
			cl = cl + c;
		}
		word.len = n - word.ptr;
		for (; n < e; n++) {
			if (!HTTP_IS_LWS(*n)) {
				if (unlikely(*n != ','))
					goto fail;
				break;
			}
		}
		if (check_prev && cl != *body_len)
			goto fail;
		*body_len = cl;
		*value = word;
		word.ptr = n;
		check_prev = 1;
	}
	return !not_first;
 fail:
	return -1;
}

int key_reject_and_link(struct key *key,
			unsigned timeout,
			unsigned error,
			struct key *keyring,
			struct key *authkey)
{
	struct assoc_array_edit *edit;
	struct timespec now;
	int ret, awaken, link_ret = 0;
	key_check(key);
	key_check(keyring);
	awaken = 0;
	ret = -EBUSY;
	if (keyring) {
		if (keyring->restrict_link)
			return -EPERM;
		link_ret = __key_link_begin(keyring, &key->index_key, &edit);
	}
	mutex_lock(&key_construction_mutex);
	if (key->state == KEY_IS_UNINSTANTIATED) {
		atomic_inc(&key->user->nikeys);
		mark_key_instantiated(key, -error);
		now = current_kernel_time();
		key->expiry = now.tv_sec + timeout;
		key_schedule_gc(key->expiry + key_gc_delay);
		if (test_and_clear_bit(KEY_FLAG_USER_CONSTRUCT, &key->flags))
			awaken = 1;
		ret = 0;
		if (keyring && link_ret == 0)
			__key_link(key, &edit);
		if (authkey)
			key_revoke(authkey);
	}
	mutex_unlock(&key_construction_mutex);
	if (keyring && link_ret == 0)
		__key_link_end(keyring, &key->index_key, edit);
	if (awaken)
		wake_up_bit(&key->flags, KEY_FLAG_USER_CONSTRUCT);
	return ret == 0 ? link_ret : ret;
}

static int32_t check_load_area(uintptr_t dst, uintptr_t len)
{
	uint32_t legacy = dst + len <= UINT32_MAX - 1 ? 1 : 0;
	uintptr_t dram_start, dram_end;
	uintptr_t prot_start, prot_end;
	int32_t result = IO_SUCCESS;
	dram_start = legacy ? DRAM1_BASE : DRAM_40BIT_BASE;
	dram_end = legacy ? DRAM1_BASE + DRAM1_SIZE :
	    DRAM_40BIT_BASE + DRAM_40BIT_SIZE;
	prot_start = legacy ? DRAM_PROTECTED_BASE : DRAM_40BIT_PROTECTED_BASE;
	prot_end = prot_start + DRAM_PROTECTED_SIZE;
	if (dst < dram_start || dst > dram_end - len) {
		ERROR("BL2: dst address is on the protected area.\n");
		result = IO_FAIL;
		goto done;
	}
	if (dst >= prot_start && dst < prot_end) {
		ERROR("BL2: dst address is on the protected area.\n");
		result = IO_FAIL;
	}
	if (dst < prot_start && dst > prot_start - len) {
		ERROR("BL2: loaded data is on the protected area.\n");
		result = IO_FAIL;
	}
done:
	if (result == IO_FAIL) {
		ERROR("BL2: Out of range : dst=0x%lx len=0x%lx\n", dst, len);
	}
	return result;
}

