Error ImageOverlay::parse(size_t num_images, const std::vector<uint8_t>& data)
{
  Error eofError(heif_error_Invalid_input,
                 heif_suberror_Invalid_grid_data,
                 "Overlay image data incomplete");
  if (data.size() < 2 + 4 * 2) {
    return eofError;
  }
  m_version = data[0];
  m_flags = data[1];
  if (m_version != 0) {
    std::stringstream sstr;
    sstr << "Overlay image data version " << ((int) m_version) << " is not implemented yet";
    return Error(heif_error_Unsupported_feature,
                 heif_suberror_Unsupported_data_version,
                 sstr.str());
  }
  int field_len = ((m_flags & 1) ? 4 : 2);
  int ptr = 2;
  if (ptr + 4 * 2 + 2 * field_len + num_images * 2 * field_len > data.size()) {
    return eofError;
  }
  for (int i = 0; i < 4; i++) {
    uint16_t color = static_cast<uint16_t>(readvec(data, ptr, 2));
    m_background_color[i] = color;
  }
  m_width = readvec(data, ptr, field_len);
  m_height = readvec(data, ptr, field_len);
  m_offsets.resize(num_images);
  for (size_t i = 0; i < num_images; i++) {
    m_offsets[i].x = readvec_signed(data, ptr, field_len);
    m_offsets[i].y = readvec_signed(data, ptr, field_len);
  }
  return Error::Ok;
}

static int link_pipe(struct pipe_inode_info *ipipe,
		     struct pipe_inode_info *opipe,
		     size_t len, unsigned int flags)
{
	struct pipe_buffer *ibuf, *obuf;
	int ret = 0, i = 0, nbuf;
	pipe_double_lock(ipipe, opipe);
	do {
		if (!opipe->readers) {
			send_sig(SIGPIPE, current, 0);
			if (!ret)
				ret = -EPIPE;
			break;
		}
		if (i >= ipipe->nrbufs || opipe->nrbufs >= opipe->buffers)
			break;
		ibuf = ipipe->bufs + ((ipipe->curbuf + i) & (ipipe->buffers-1));
		nbuf = (opipe->curbuf + opipe->nrbufs) & (opipe->buffers - 1);
		pipe_buf_get(ipipe, ibuf);
		obuf = opipe->bufs + nbuf;
		*obuf = *ibuf;
		obuf->flags &= ~PIPE_BUF_FLAG_GIFT;
		if (obuf->len > len)
			obuf->len = len;
		opipe->nrbufs++;
		ret += obuf->len;
		len -= obuf->len;
		i++;
	} while (len);
	if (!ret && ipipe->waiting_writers && (flags & SPLICE_F_NONBLOCK))
		ret = -EAGAIN;
	pipe_unlock(ipipe);
	pipe_unlock(opipe);
	if (ret > 0)
		wakeup_pipe_readers(opipe);
	return ret;
}

parse_range(char *str, size_t file_sz, int *nranges)
{
	static struct range	 ranges[MAX_RANGES];
	int			 i = 0;
	char			*p, *q;
	if ((p = strchr(str, '=')) == NULL)
		return (NULL);
	*p++ = '\0';
	if (strcmp(str, "bytes") != 0)
		return (NULL);
	while ((q = strchr(p, ',')) != NULL) {
		*q++ = '\0';
		if (parse_range_spec(p, file_sz, &ranges[i]) == 0)
			continue;
		i++;
		if (i == MAX_RANGES)
			return (NULL);
		p = q;
	}
	if (parse_range_spec(p, file_sz, &ranges[i]) != 0)
		i++;
	*nranges = i;
	return (i ? ranges : NULL);
}

	__acquires(&current->sighand->siglock)
{
	bool gstop_done = false;
	if (arch_ptrace_stop_needed(exit_code, info)) {
		spin_unlock_irq(&current->sighand->siglock);
		arch_ptrace_stop(exit_code, info);
		spin_lock_irq(&current->sighand->siglock);
		if (sigkill_pending(current))
			return;
	}
	set_current_state(TASK_TRACED);
	current->last_siginfo = info;
	current->exit_code = exit_code;
	if (why == CLD_STOPPED && (current->jobctl & JOBCTL_STOP_PENDING))
		gstop_done = task_participate_group_stop(current);
	task_clear_jobctl_pending(current, JOBCTL_TRAP_STOP);
	if (info && info->si_code >> 8 == PTRACE_EVENT_STOP)
		task_clear_jobctl_pending(current, JOBCTL_TRAP_NOTIFY);
	task_clear_jobctl_trapping(current);
	spin_unlock_irq(&current->sighand->siglock);
	read_lock(&tasklist_lock);
	if (may_ptrace_stop()) {
		do_notify_parent_cldstop(current, true, why);
		if (gstop_done && ptrace_reparented(current))
			do_notify_parent_cldstop(current, false, why);
		preempt_disable();
		read_unlock(&tasklist_lock);
		preempt_enable_no_resched();
		freezable_schedule();
	} else {
		if (gstop_done)
			do_notify_parent_cldstop(current, false, why);
		__set_current_state(TASK_RUNNING);
		if (clear_code)
			current->exit_code = 0;
		read_unlock(&tasklist_lock);
	}
	spin_lock_irq(&current->sighand->siglock);
	current->last_siginfo = NULL;
	current->jobctl &= ~JOBCTL_LISTENING;
	recalc_sigpending_tsk(current);
}

