}

static struct ip_options *tcp_v4_save_options(struct sock *sk,
					      struct sk_buff *skb)
{
	struct ip_options *opt = &(IPCB(skb)->opt);
	struct ip_options *dopt = NULL;
	if (opt && opt->optlen) {
		int opt_size = optlength(opt);
		dopt = kmalloc(opt_size, GFP_ATOMIC);
		if (dopt) {
			if (ip_options_echo(dopt, skb)) {
				kfree(dopt);
				dopt = NULL;
			}
		}
	}
	return dopt;
}

int vm_iomap_memory(struct vm_area_struct *vma, phys_addr_t start, unsigned long len)
{
	unsigned long vm_len, pfn, pages;
	if (start + len < start)
		return -EINVAL;
	len += start & ~PAGE_MASK;
	pfn = start >> PAGE_SHIFT;
	pages = (len + ~PAGE_MASK) >> PAGE_SHIFT;
	if (pfn + pages < pfn)
		return -EINVAL;
	if (vma->vm_pgoff > pages)
		return -EINVAL;
	pfn += vma->vm_pgoff;
	pages -= vma->vm_pgoff;
	vm_len = vma->vm_end - vma->vm_start;
	if (vm_len >> PAGE_SHIFT > pages)
		return -EINVAL;
	return io_remap_pfn_range(vma, vma->vm_start, pfn, vm_len, vma->vm_page_prot);
}

int h1_parse_cont_len_header(struct h1m *h1m, struct ist *value)
{
	char *e, *n;
	long long cl;
	int not_first = !!(h1m->flags & H1_MF_CLEN);
	struct ist word;
	word.ptr = value->ptr - 1; 
	e = value->ptr + value->len;
	while (++word.ptr < e) {
		if (unlikely(HTTP_IS_LWS(*word.ptr)))
			continue;
		for (cl = 0, n = word.ptr; n < e; n++) {
			unsigned int c = *n - '0';
			if (unlikely(c > 9)) {
				if (unlikely(n == word.ptr)) 
					goto fail;
				break;
			}
			if (unlikely(cl > ULLONG_MAX / 10ULL))
				goto fail; 
			cl = cl * 10ULL;
			if (unlikely(cl + c < cl))
				goto fail; 
			cl = cl + c;
		}
		word.len = n - word.ptr;
		for (; n < e; n++) {
			if (!HTTP_IS_LWS(*n)) {
				if (unlikely(*n != ','))
					goto fail;
				break;
			}
		}
		if (h1m->flags & H1_MF_CLEN && cl != h1m->body_len)
			goto fail;
		h1m->flags |= H1_MF_CLEN;
		h1m->curr_len = h1m->body_len = cl;
		*value = word;
		word.ptr = n;
	}
	return !not_first;
 fail:
	return -1;
}

enum nss_status _nss_mymachines_getgrnam_r(
                const char *name,
                struct group *gr,
                char *buffer, size_t buflen,
                int *errnop) {
        _cleanup_bus_error_free_ sd_bus_error error = SD_BUS_ERROR_NULL;
        _cleanup_bus_message_unref_ sd_bus_message* reply = NULL;
        _cleanup_bus_flush_close_unref_ sd_bus *bus = NULL;
        const char *p, *e, *machine;
        uint32_t mapped;
        uid_t gid;
        size_t l;
        int r;
        assert(name);
        assert(gr);
        p = startswith(name, "vg-");
        if (!p)
                goto not_found;
        e = strrchr(p, '-');
        if (!e || e == p)
                goto not_found;
        if (e - p > HOST_NAME_MAX - 1)  
                goto not_found;
        r = parse_gid(e + 1, &gid);
        if (r < 0)
                goto not_found;
        machine = strndupa(p, e - p);
        if (!machine_name_is_valid(machine))
                goto not_found;
        r = sd_bus_open_system(&bus);
        if (r < 0)
                goto fail;
        r = sd_bus_call_method(bus,
                               "org.freedesktop.machine1",
                               "/org/freedesktop/machine1",
                               "org.freedesktop.machine1.Manager",
                               "MapFromMachineGroup",
                               &error,
                               &reply,
                               "su",
                               machine, (uint32_t) gid);
        if (r < 0) {
                if (sd_bus_error_has_name(&error, BUS_ERROR_NO_SUCH_GROUP_MAPPING))
                        goto not_found;
                goto fail;
        }
        r = sd_bus_message_read(reply, "u", &mapped);
        if (r < 0)
                goto fail;
        l = sizeof(char*) + strlen(name) + 1;
        if (buflen < l) {
                *errnop = ENOMEM;
                return NSS_STATUS_TRYAGAIN;
        }
        memzero(buffer, sizeof(char*));
        strcpy(buffer + sizeof(char*), name);
        gr->gr_name = buffer + sizeof(char*);
        gr->gr_gid = gid;
        gr->gr_passwd = (char*) "*"; 
        gr->gr_mem = (char**) buffer;
        *errnop = 0;
        return NSS_STATUS_SUCCESS;
not_found:
        *errnop = 0;
        return NSS_STATUS_NOTFOUND;
fail:
        *errnop = -r;
        return NSS_STATUS_UNAVAIL;
}

int bridge__remap_topic_in(struct mosquitto *context, char **topic)
{
	struct mosquitto__bridge_topic *cur_topic;
	char *topic_temp;
	int i;
	size_t len;
	int rc;
	bool match;
	if(context->bridge && context->bridge->topics && context->bridge->topic_remapping){
		for(i=0; i<context->bridge->topic_count; i++){
			cur_topic = &context->bridge->topics[i];
			if((cur_topic->direction == bd_both || cur_topic->direction == bd_in)
					&& (cur_topic->remote_prefix || cur_topic->local_prefix)){
				rc = mosquitto_topic_matches_sub(cur_topic->remote_topic, *topic, &match);
				if(rc){
					mosquitto__free(*topic);
					*topic = NULL;
					return rc;
				}
				if(match){
					if(cur_topic->remote_prefix){
						if(!strncmp(cur_topic->remote_prefix, *topic, strlen(cur_topic->remote_prefix))){
							topic_temp = mosquitto__strdup((*topic)+strlen(cur_topic->remote_prefix));
							if(!topic_temp){
								mosquitto__free(*topic);
								*topic = NULL;
								return MOSQ_ERR_NOMEM;
							}
							mosquitto__free(*topic);
							*topic = topic_temp;
						}
					}
					if(cur_topic->local_prefix){
						len = strlen(*topic) + strlen(cur_topic->local_prefix)+1;
						topic_temp = mosquitto__malloc(len+1);
						if(!topic_temp){
							mosquitto__free(*topic);
							*topic = NULL;
							return MOSQ_ERR_NOMEM;
						}
						snprintf(topic_temp, len, "%s%s", cur_topic->local_prefix, *topic);
						topic_temp[len] = '\0';
						mosquitto__free(*topic);
						*topic = topic_temp;
					}
					break;
				}
			}
		}
	}
	return MOSQ_ERR_SUCCESS;
}

bool LinksToDirs(const wchar *SrcName,const wchar *SkipPart,std::wstring &LastChecked)
{
  wchar Path[NM];
  if (wcslen(SrcName)>=ASIZE(Path))
    return false;  
  wcsncpyz(Path,SrcName,ASIZE(Path));
  size_t SkipLength=wcslen(SkipPart);
  if (SkipLength>0 && wcsncmp(Path,SkipPart,SkipLength)!=0)
    SkipLength=0; 
  for (uint I=0;Path[I]!=0 && I<LastChecked.size() && Path[I]==LastChecked[I];I++)
    if (IsPathDiv(Path[I]) && I>SkipLength)
      SkipLength=I;
  wchar *Name=Path;
  if (SkipLength>0)
  {
    Name+=SkipLength;
    while (IsPathDiv(*Name))
      Name++;
  }
  for (wchar *s=Path+wcslen(Path)-1;s>Name;s--)
    if (IsPathDiv(*s))
    {
      *s=0;
      FindData FD;
      if (FindFile::FastFind(Path,&FD,true) && FD.IsLink)
#ifdef _WIN_ALL
        if (!DelDir(Path))
#else
        if (!DelFile(Path))
#endif
          return false; 
    }
  LastChecked=SrcName;
  return true;
}

static void mmtimer_setup_int_0(int cpu, u64 expires)
{
	u64 val;
	HUB_S((u64 *)LOCAL_MMR_ADDR(SH_RTC1_INT_ENABLE), 0UL);
	HUB_S((u64 *)LOCAL_MMR_ADDR(SH_INT_CMPB), -1L);
	mmtimer_clr_int_pending(0);
	val = ((u64)SGI_MMTIMER_VECTOR << SH_RTC1_INT_CONFIG_IDX_SHFT) |
		((u64)cpu_physical_id(cpu) <<
			SH_RTC1_INT_CONFIG_PID_SHFT);
