int _gnutls_x509_verify_signature(const gnutls_datum_t * tbs,
				  const gnutls_datum_t * signature,
				  gnutls_x509_crt_t issuer)
{
    mpi_t issuer_params[MAX_PUBLIC_PARAMS_SIZE];
    int ret, issuer_params_size, i;
    issuer_params_size = MAX_PUBLIC_PARAMS_SIZE;
    ret =
	_gnutls_x509_crt_get_mpis(issuer, issuer_params,
				  &issuer_params_size);
    if (ret < 0) {
	gnutls_assert();
	return ret;
    }
    ret =
	verify_sig(tbs, signature,
		   gnutls_x509_crt_get_pk_algorithm(issuer, NULL),
		   issuer_params, issuer_params_size);
    if (ret < 0) {
	gnutls_assert();
    }
    for (i = 0; i < issuer_params_size; i++) {
	_gnutls_mpi_release(&issuer_params[i]);
    }
    return ret;
}

static OPJ_BOOL bmp_read_rle8_data(FILE* IN, OPJ_UINT8* pData,
                                   OPJ_UINT32 stride, OPJ_UINT32 width, OPJ_UINT32 height)
{
   OPJ_UINT32 x, y;
    OPJ_UINT8 *pix;
    const OPJ_UINT8 *beyond;
    beyond = pData + stride * height;
    pix = pData;
   x = y = 0U;
    while (y < height) {
        int c = getc(IN);
        if (c == EOF) {
           return OPJ_FALSE;
       }
       if (c) {
           int j, c1_int;
           OPJ_UINT8 c1;
           c1_int = getc(IN);
           if (c1_int == EOF) {
               return OPJ_FALSE;
           }
           c1 = (OPJ_UINT8)c1_int;
            for (j = 0; (j < c) && (x < width) &&
                    ((OPJ_SIZE_T)pix < (OPJ_SIZE_T)beyond); j++, x++, pix++) {
                *pix = c1;
            }
        } else {
            c = getc(IN);
           if (c == EOF) {
               return OPJ_FALSE;
           }
           if (c == 0x00) { 
               x = 0;
               ++y;
               pix = pData + y * stride + x;
           } else if (c == 0x01) { 
               break;
           } else if (c == 0x02) { 
               c = getc(IN);
               if (c == EOF) {
                   return OPJ_FALSE;
               }
               x += (OPJ_UINT32)c;
               c = getc(IN);
               if (c == EOF) {
                   return OPJ_FALSE;
               }
               y += (OPJ_UINT32)c;
               pix = pData + y * stride + x;
           } else { 
               int j;
               for (j = 0; (j < c) && (x < width) &&
                       ((OPJ_SIZE_T)pix < (OPJ_SIZE_T)beyond); j++, x++, pix++) {
                   int c1_int;
                   OPJ_UINT8 c1;
                   c1_int = getc(IN);
                   if (c1_int == EOF) {
                       return OPJ_FALSE;
                    }
                    c1 = (OPJ_UINT8)c1_int;
                    *pix = c1;
                }
                if ((OPJ_UINT32)c & 1U) { 
                    c = getc(IN);
                   if (c == EOF) {
                       return OPJ_FALSE;
                   }
               }
            }
        }
    }
    return OPJ_TRUE;
}

EventReturn OnPreCommand(CommandSource &source, Command *command, std::vector<Anope::string> &params) anope_override
{
	if (command->name == "nickserv/confirm" && params.size() > 1)
	{
		if (Anope::ReadOnly)
		{
			source.Reply(READ_ONLY_MODE);
			return EVENT_STOP;
		}
		NickAlias *na = NickAlias::Find(params[0]);
		ResetInfo *ri = na ? reset.Get(na->nc) : NULL;
		if (na && ri)
		{
			NickCore *nc = na->nc;
			if (nc->HasExt("NS_SUSPENDED"))
			{
				source.Reply(NICK_X_SUSPENDED, nc->display.c_str());
				return EVENT_STOP;
			}
			const Anope::string &passcode = params[1];
			if (ri->time < Anope::CurTime - 3600)
			{
				reset.Unset(nc);
				source.Reply(_("Your password reset request has expired."));
			}
			else if (passcode.equals_cs(ri->code))
			{
				reset.Unset(nc);
				nc->Shrink<bool>("UNCONFIRMED");
				Log(LOG_COMMAND, source, &commandnsresetpass) << "to confirm RESETPASS and forcefully identify as " << na->nick;
				if (source.GetUser())
				{
					source.GetUser()->Identify(na);
				}
				source.Reply(_("You are now identified for your nick. Change your password now."));
			}
			else
				return EVENT_CONTINUE;
			return EVENT_STOP;
		}
	}
	return EVENT_CONTINUE;
}

    bool wc2mb(UINT cp, std::string& str)
    {
        if (str.empty())
            return true;
        if (str.size() & 1) {
#ifdef DEBUG
            EXV_DEBUG << "wc2mb: Size " << str.size() << " of input string is not even.\n";
#endif
            return false;
        }
        int len = WideCharToMultiByte(cp, 0, (LPCWSTR)str.data(), (int)str.size() / 2, 0, 0, 0, 0);
        if (len == 0) {
#ifdef DEBUG
            EXV_DEBUG << "wc2mb: Failed to determine required size of output buffer.\n";
#endif
            return false;
        }
        std::vector<std::string::value_type> out;
        out.reserve(len);
        int ret = WideCharToMultiByte(cp, 0, (LPCWSTR)str.data(), (int)str.size() / 2, (LPSTR)out.data(), len, 0, 0);
        if (ret == 0) {
#ifdef DEBUG
            EXV_DEBUG << "wc2mb: Failed to convert the input string to a multi byte string.\n";
#endif
            return false;
        }
        str.assign(out.data(), static_cast<size_t>(len));
        return true;
    }

ExprResolveEnum(struct xkb_context *ctx, const ExprDef *expr,
                unsigned int *val_rtrn, const LookupEntry *values)
{
    if (expr->expr.op != EXPR_IDENT) {
        log_err(ctx, "Found a %s where an enumerated value was expected\n",
                expr_op_type_to_string(expr->expr.op));
        return false;
    }
    if (!SimpleLookup(ctx, values, expr->ident.ident, EXPR_TYPE_INT,
                      val_rtrn)) {
        log_err(ctx, "Illegal identifier %s; expected one of:\n",
                xkb_atom_text(ctx, expr->ident.ident));
        while (values && values->name)
        {
            log_err(ctx, "\t%s\n", values->name);
            values++;
        }
        return false;
    }
    return true;
}

path_in(PG_FUNCTION_ARGS)
{
	char	   *str = PG_GETARG_CSTRING(0);
	PATH	   *path;
	int			isopen;
 	char	   *s;
 	int			npts;
 	int			size;
 	int			depth = 0;
 	if ((npts = pair_count(str, ',')) <= 0)
		ereport(ERROR,
				(errcode(ERRCODE_INVALID_TEXT_REPRESENTATION),
				 errmsg("invalid input syntax for type path: \"%s\"", str)));
	s = str;
	while (isspace((unsigned char) *s))
		s++;
	if ((*s == LDELIM) && (strrchr(s, LDELIM) == s))
	{
		s++;
 		depth++;
 	}
	size = offsetof(PATH, p[0]) +sizeof(path->p[0]) * npts;
 	path = (PATH *) palloc(size);
 	SET_VARSIZE(path, size);
	path->npts = npts;
	if ((!path_decode(TRUE, npts, s, &isopen, &s, &(path->p[0])))
	&& (!((depth == 0) && (*s == '\0'))) && !((depth >= 1) && (*s == RDELIM)))
		ereport(ERROR,
				(errcode(ERRCODE_INVALID_TEXT_REPRESENTATION),
				 errmsg("invalid input syntax for type path: \"%s\"", str)));
	path->closed = (!isopen);
	path->dummy = 0;
	PG_RETURN_PATH_P(path);
}

void Node::RunForwardTypeInference() {
  VLOG(4) << "Forward type inference: " << props_->node_def.DebugString();
  if (props_->fwd_type_fn == nullptr) {
    return;
  }
  std::vector<Node*> input_nodes(props_->input_types.size(), nullptr);
  std::vector<int> input_idx(props_->input_types.size(), 0);
  for (const auto& edge : in_edges_) {
    if (edge->IsControlEdge()) {
      continue;
    }
    DCHECK(edge->dst_input() < input_nodes.size()) << DebugString();
    int i = edge->dst_input();
    input_nodes.at(i) = edge->src();
    input_idx.at(i) = edge->src_output();
  }
  for (const auto* node : input_nodes) {
    if (node == nullptr) {
      ClearTypeInfo();
      return;
    }
  }
  static FullTypeDef* no_type = new FullTypeDef();
  std::vector<std::reference_wrapper<const FullTypeDef>> input_types;
  for (int i = 0; i < input_nodes.size(); i++) {
    const auto* node = input_nodes[i];
    if (node->def().has_experimental_type()) {
      const auto& node_t = node->def().experimental_type();
      if (node_t.type_id() != TFT_UNSET) {
        int ix = input_idx[i];
        DCHECK(ix < node_t.args_size())
            << "input " << i << " should have an output " << ix
            << " but instead only has " << node_t.args_size()
            << " outputs: " << node_t.DebugString();
        input_types.emplace_back(node_t.args(ix));
      } else {
        input_types.emplace_back(*no_type);
      }
    } else {
      ClearTypeInfo();
      return;
    }
  }
  const auto infer_type = props_->fwd_type_fn(input_types);
  const FullTypeDef infer_typedef = infer_type.ValueOrDie();
  if (infer_typedef.type_id() != TFT_UNSET) {
    MaybeCopyOnWrite();
    *(props_->node_def.mutable_experimental_type()) = infer_typedef;
  }
}

