order_notification_deltas(struct rdr_notification_ctx *ctx)
{
	struct delta_head **ptr;
	array_index i;
	int error;
	error = deltas_head_set_size(ctx->notification->deltas_list,
	    ctx->deltas.len);
	if (error)
		return error;
	ARRAYLIST_FOREACH(&ctx->deltas, ptr, i) {
		error = deltas_head_add(ctx->notification->deltas_list,
		    ctx->notification->global_data.serial,
		    (*ptr)->serial,
		    (*ptr)->doc_data.uri,
		    (*ptr)->doc_data.hash,
		    (*ptr)->doc_data.hash_len);
		if (!error)
			continue;
		if (error == -EINVAL)
			return pr_val_err("Serial '%lu' at delta elements isn't part of a contiguous list of serials.",
			    (*ptr)->serial);
		if (error == -EEXIST)
			return pr_val_err("Duplicated serial '%lu' at delta elements.",
			    (*ptr)->serial);
		return error;
	}
	if (!deltas_head_values_set(ctx->notification->deltas_list))
		return pr_val_err("Deltas listed don't have a contiguous sequence of serial numbers");
	return 0;
}

static int ssi_sd_load(QEMUFile *f, void *opaque, int version_id)
{
    SSISlave *ss = SSI_SLAVE(opaque);
    ssi_sd_state *s = (ssi_sd_state *)opaque;
    int i;
    if (version_id != 1)
        return -EINVAL;
    s->mode = qemu_get_be32(f);
    s->cmd = qemu_get_be32(f);
    for (i = 0; i < 4; i++)
        s->cmdarg[i] = qemu_get_be32(f);
    for (i = 0; i < 5; i++)
        s->response[i] = qemu_get_be32(f);
    s->arglen = qemu_get_be32(f);
    s->response_pos = qemu_get_be32(f);
    s->stopping = qemu_get_be32(f);
    ss->cs = qemu_get_be32(f);
    return 0;
}

void Compute(OpKernelContext* ctx) override {
  const auto splits = ctx->input(0).flat<int64_t>();
  const auto values = ctx->input(1).flat<Tidx>();
  const Tensor& size_t = ctx->input(2);
  const auto weights = ctx->input(3).flat<T>();
  const int64_t weights_size = weights.size();
  OP_REQUIRES(ctx, size_t.dims() == 0,
              errors::InvalidArgument("Shape must be rank 0 but is rank ",
                                      size_t.dims()));
  Tidx size = size_t.scalar<Tidx>()();
  OP_REQUIRES(
      ctx, size >= 0,
      errors::InvalidArgument("size (", size, ") must be non-negative"));
  int num_rows = splits.size() - 1;
  int num_values = values.size();
  int batch_idx = 0;
  OP_REQUIRES(ctx, splits.size() > 0,
              errors::InvalidArgument("Splits must be non-empty"));
  OP_REQUIRES(ctx, splits(0) == 0,
              errors::InvalidArgument("Splits must start with 0, not with ",
                                      splits(0)));
  OP_REQUIRES(ctx, splits(num_rows) == num_values,
              errors::InvalidArgument(
                  "Splits must end with the number of values, got ",
                  splits(num_rows), " instead of ", num_values));
  Tensor* out_t;
  OP_REQUIRES_OK(
      ctx, ctx->allocate_output(0, TensorShape({num_rows, size}), &out_t));
  functor::SetZeroFunctor<Device, T> fill;
  fill(ctx->eigen_device<Device>(), out_t->flat<T>());
  const auto out = out_t->matrix<T>();
  for (int idx = 0; idx < num_values; ++idx) {
    while (idx >= splits(batch_idx)) {
      batch_idx++;
    }
    Tidx bin = values(idx);
    OP_REQUIRES(ctx, bin >= 0,
                errors::InvalidArgument("Input must be non-negative"));
    if (bin < size) {
      if (binary_output_) {
        out(batch_idx - 1, bin) = T(1);
      } else {
        T value = (weights_size > 0) ? weights(idx) : T(1);
        out(batch_idx - 1, bin) += value;
      }
    }
  }
}

void imap_quote_string(char *dest, size_t dlen, const char *src)
{
  static const char quote[] = "\"\\";
  char *pt = dest;
  const char *s = src;
  *pt++ = '"';
  dlen -= 2;
  for (; *s && dlen; s++)
  {
    if (strchr(quote, *s))
    {
      dlen -= 2;
      if (dlen == 0)
        break;
      *pt++ = '\\';
      *pt++ = *s;
    }
    else
    {
      *pt++ = *s;
      dlen--;
    }
  }
  *pt++ = '"';
  *pt = '\0';
}

static void __vmx_complete_interrupts(struct kvm_vcpu *vcpu,
				      u32 idt_vectoring_info,
				      int instr_len_field,
				      int error_code_field)
{
	u8 vector;
	int type;
	bool idtv_info_valid;
	idtv_info_valid = idt_vectoring_info & VECTORING_INFO_VALID_MASK;
	vcpu->arch.nmi_injected = false;
	kvm_clear_exception_queue(vcpu);
	kvm_clear_interrupt_queue(vcpu);
	if (!idtv_info_valid)
		return;
	kvm_make_request(KVM_REQ_EVENT, vcpu);
	vector = idt_vectoring_info & VECTORING_INFO_VECTOR_MASK;
	type = idt_vectoring_info & VECTORING_INFO_TYPE_MASK;
	switch (type) {
	case INTR_TYPE_NMI_INTR:
		vcpu->arch.nmi_injected = true;
		vmx_set_nmi_mask(vcpu, false);
		break;
	case INTR_TYPE_SOFT_EXCEPTION:
		vcpu->arch.event_exit_inst_len = vmcs_read32(instr_len_field);
	case INTR_TYPE_HARD_EXCEPTION:
		if (idt_vectoring_info & VECTORING_INFO_DELIVER_CODE_MASK) {
			u32 err = vmcs_read32(error_code_field);
			kvm_requeue_exception_e(vcpu, vector, err);
		} else
			kvm_requeue_exception(vcpu, vector);
		break;
	case INTR_TYPE_SOFT_INTR:
		vcpu->arch.event_exit_inst_len = vmcs_read32(instr_len_field);
	case INTR_TYPE_EXT_INTR:
		kvm_queue_interrupt(vcpu, vector, type == INTR_TYPE_SOFT_INTR);
		break;
	default:
		break;
	}
}

