		printk(KERN_ERR "Unable to locate \"tpgt_#\" directory"
				" group\n");
		return ERR_PTR(-EINVAL);
	}
	tpgt_str += 5; 
	tpgt = (unsigned short int) simple_strtoul(tpgt_str, &end_ptr, 0);
