static MagickBooleanType CheckPrimitiveExtent(MVGInfo *mvg_info,
  const double pad)
{
  double
    extent;
  size_t
    quantum;
  quantum=sizeof(**mvg_info->primitive_info);
  extent=(double) mvg_info->offset+pad+(PrimitiveExtentPad+1)*quantum;
  if (extent <= (double) *mvg_info->extent)
    return(MagickTrue);
  if (extent == (double) CastDoubleToLong(extent))
    {
      *mvg_info->primitive_info=(PrimitiveInfo *) ResizeQuantumMemory(
        *mvg_info->primitive_info,(size_t) (extent+1),quantum);
      if (*mvg_info->primitive_info != (PrimitiveInfo *) NULL)
        {
          ssize_t
            i;
          *mvg_info->extent=(size_t) extent;
          for (i=mvg_info->offset+1; i <= (ssize_t) extent; i++)
          {
            (*mvg_info->primitive_info)[i].primitive=UndefinedPrimitive;
            (*mvg_info->primitive_info)[i].text=(char *) NULL;
          }
          return(MagickTrue);
        }
    }
  (void) ThrowMagickException(mvg_info->exception,GetMagickModule(),
    ResourceLimitError,"MemoryAllocationFailed","`%s'","");
  if (*mvg_info->primitive_info != (PrimitiveInfo *) NULL)
    *mvg_info->primitive_info=(PrimitiveInfo *) RelinquishMagickMemory(
      *mvg_info->primitive_info);
  *mvg_info->primitive_info=(PrimitiveInfo *) AcquireCriticalMemory((size_t) (
    (PrimitiveExtentPad+1)*quantum));
  (void) memset(*mvg_info->primitive_info,0,(size_t) ((PrimitiveExtentPad+1)*
    quantum));
  *mvg_info->extent=1;
  mvg_info->offset=0;
  return(MagickFalse);
}

CopyKeyAliasesToKeymap(struct xkb_keymap *keymap, KeyNamesInfo *info)
{
    AliasInfo *alias;
    unsigned i, num_key_aliases;
    struct xkb_key_alias *key_aliases;
    num_key_aliases = 0;
    darray_foreach(alias, info->aliases) {
        if (!XkbKeyByName(keymap, alias->real, false)) {
            log_vrb(info->ctx, 5,
                    "Attempt to alias %s to non-existent key %s; Ignored\n",
                    KeyNameText(info->ctx, alias->alias),
                    KeyNameText(info->ctx, alias->real));
            alias->real = XKB_ATOM_NONE;
            continue;
        }
        if (XkbKeyByName(keymap, alias->alias, false)) {
            log_vrb(info->ctx, 5,
                    "Attempt to create alias with the name of a real key; "
                    "Alias \"%s = %s\" ignored\n",
                    KeyNameText(info->ctx, alias->alias),
                    KeyNameText(info->ctx, alias->real));
            alias->real = XKB_ATOM_NONE;
            continue;
        }
        num_key_aliases++;
    }
    key_aliases = NULL;
    if (num_key_aliases > 0) {
         key_aliases = calloc(num_key_aliases, sizeof(*key_aliases));
         if (!key_aliases)
             return false;
    }
    i = 0;
    darray_foreach(alias, info->aliases) {
        if (alias->real != XKB_ATOM_NONE) {
            key_aliases[i].alias = alias->alias;
            key_aliases[i].real = alias->real;
            i++;
         }
     }
    keymap->num_key_aliases = num_key_aliases;
    keymap->key_aliases = key_aliases;
    return true;
}

void MakeDataset(OpKernelContext* ctx, DatasetBase** output) override {
  const Tensor* indices;
  OP_REQUIRES_OK(ctx, ctx->input("indices", &indices));
  const Tensor* values;
  OP_REQUIRES_OK(ctx, ctx->input("values", &values));
  const Tensor* dense_shape;
  OP_REQUIRES_OK(ctx, ctx->input("dense_shape", &dense_shape));
  OP_REQUIRES(ctx, TensorShapeUtils::IsMatrix(indices->shape()),
              errors::InvalidArgument(
                  "Input indices should be a matrix but received shape ",
                  indices->shape().DebugString()));
  const auto num_indices = indices->NumElements();
  const auto num_values = values->NumElements();
  if (num_indices == 0 || num_values == 0) {
    OP_REQUIRES(ctx, num_indices == num_values,
                errors::InvalidArgument(
                    "If indices or values are empty, the other one must also "
                    "be. Got indices of shape ",
                    indices->shape().DebugString(), " and values of shape ",
                    values->shape().DebugString()));
  }
  OP_REQUIRES(ctx, TensorShapeUtils::IsVector(values->shape()),
              errors::InvalidArgument(
                  "Input values should be a vector but received shape ",
                  indices->shape().DebugString()));
  OP_REQUIRES(ctx, TensorShapeUtils::IsVector(dense_shape->shape()),
              errors::InvalidArgument(
                  "Input shape should be a vector but received shape ",
                  dense_shape->shape().DebugString()));
  int64_t previous_batch_index = -1;
  for (int64_t i = 0; i < indices->dim_size(0); ++i) {
    int64_t next_batch_index = indices->matrix<int64_t>()(i, 0);
    OP_REQUIRES(
        ctx, next_batch_index >= previous_batch_index,
        errors::Unimplemented("The SparseTensor must be ordered in the batch "
                              "dimension; handling arbitrarily ordered input "
                              "is not currently supported."));
    previous_batch_index = next_batch_index;
  }
  gtl::InlinedVector<int64_t, 8> std_order(dense_shape->NumElements(), 0);
  sparse::SparseTensor tensor;
  OP_REQUIRES_OK(
      ctx, sparse::SparseTensor::Create(
               *indices, *values, TensorShape(dense_shape->vec<int64_t>()),
               std_order, &tensor));
  *output = new Dataset<T>(ctx, std::move(tensor));
}

GIT_INLINE(bool) only_spaces_and_dots(const char *path)
{
	const char *c = path;
	for (;; c++) {
		if (*c == '\0')
			return true;
		if (*c != ' ' && *c != '.')
			return false;
	}
	return true;
}

void LibRaw::fuji_compressed_load_raw()
{
  struct fuji_compressed_params common_info;
  int cur_block;
  unsigned line_size, *block_sizes;
  INT64 raw_offset, *raw_block_offsets;
  init_fuji_compr(&common_info);
  line_size = sizeof(ushort) * (common_info.line_width + 2);
  block_sizes = (unsigned *)malloc(
      sizeof(unsigned) * libraw_internal_data.unpacker_data.fuji_total_blocks);
  merror(block_sizes, "fuji_compressed_load_raw()");
  raw_block_offsets = (INT64 *)malloc(
      sizeof(INT64) * libraw_internal_data.unpacker_data.fuji_total_blocks);
  merror(raw_block_offsets, "fuji_compressed_load_raw()");
  raw_offset =
      sizeof(unsigned) * libraw_internal_data.unpacker_data.fuji_total_blocks;
  if (raw_offset & 0xC)
    raw_offset += 0x10 - (raw_offset & 0xC);
  raw_offset += libraw_internal_data.unpacker_data.data_offset;
  libraw_internal_data.internal_data.input->seek(
      libraw_internal_data.unpacker_data.data_offset, SEEK_SET);
  libraw_internal_data.internal_data.input->read(
      block_sizes, 1,
      sizeof(unsigned) * libraw_internal_data.unpacker_data.fuji_total_blocks);
  raw_block_offsets[0] = raw_offset;
  for (cur_block = 0;
       cur_block < libraw_internal_data.unpacker_data.fuji_total_blocks;
       cur_block++)
  {
    unsigned bsize = sgetn(4, (uchar *)(block_sizes + cur_block));
    block_sizes[cur_block] = bsize;
  }
  for (cur_block = 1;
       cur_block < libraw_internal_data.unpacker_data.fuji_total_blocks;
       cur_block++)
    raw_block_offsets[cur_block] =
        raw_block_offsets[cur_block - 1] + block_sizes[cur_block - 1];
  fuji_decode_loop(&common_info,
                   libraw_internal_data.unpacker_data.fuji_total_blocks,
                   raw_block_offsets, block_sizes);
  free(block_sizes);
  free(raw_block_offsets);
  free(common_info.q_table);
}

