static void *skcipher_bind(const char *name, u32 type, u32 mask)
{
	struct skcipher_tfm *tfm;
	struct crypto_ablkcipher *skcipher;
	tfm = kzalloc(sizeof(*tfm), GFP_KERNEL);
	if (!tfm)
		return ERR_PTR(-ENOMEM);
	skcipher = crypto_alloc_ablkcipher(name, type, mask);
	if (IS_ERR(skcipher)) {
		kfree(tfm);
		return ERR_CAST(skcipher);
	}
	tfm->skcipher = skcipher;
	return tfm;
}

static int decode_fill(AACContext *ac, GetBitContext *gb, int len) {
    uint8_t buf[256];
    int i, major, minor;
    if (len < 13+7*8)
        goto unknown;
    get_bits(gb, 13); len -= 13;
    for(i=0; i+1<sizeof(buf) && len>=8; i++, len-=8)
        buf[i] = get_bits(gb, 8);
    buf[i] = 0;
    if (ac->avctx->debug & FF_DEBUG_PICT_INFO)
        av_log(ac->avctx, AV_LOG_DEBUG, "FILL:%s\n", buf);
    if (sscanf(buf, "libfaac %d.%d", &major, &minor) == 2){
        ac->avctx->internal->skip_samples = 1024;
    }
unknown:
    skip_bits_long(gb, len);
    return 0;
}

resolve_superior_type_check(struct lys_type *type)
{
    uint32_t i;
    if (type->base == LY_TYPE_DER) {
        return EXIT_FAILURE;
    } else if (type->base == LY_TYPE_UNION) {
        for (i = 0; i < type->info.uni.count; ++i) {
            if (resolve_superior_type_check(&type->info.uni.types[i])) {
                return EXIT_FAILURE;
            }
        }
    } else if (type->base == LY_TYPE_LEAFREF) {
        while (!type->info.lref.path) {
            assert(type->der);
            type = &type->der->type;
        }
    }
    return EXIT_SUCCESS;
}

