int vfs_clone_file_range(struct file *file_in, loff_t pos_in,
		struct file *file_out, loff_t pos_out, u64 len)
{
	struct inode *inode_in = file_inode(file_in);
	struct inode *inode_out = file_inode(file_out);
	int ret;
	if (inode_in->i_sb != inode_out->i_sb ||
	    file_in->f_path.mnt != file_out->f_path.mnt)
		return -EXDEV;
	if (S_ISDIR(inode_in->i_mode) || S_ISDIR(inode_out->i_mode))
		return -EISDIR;
	if (!S_ISREG(inode_in->i_mode) || !S_ISREG(inode_out->i_mode))
		return -EINVAL;
	if (!(file_in->f_mode & FMODE_READ) ||
	    !(file_out->f_mode & FMODE_WRITE) ||
	    (file_out->f_flags & O_APPEND) ||
	    !file_in->f_op->clone_file_range)
		return -EBADF;
	ret = clone_verify_area(file_in, pos_in, len, false);
	if (ret)
		return ret;
	ret = clone_verify_area(file_out, pos_out, len, true);
	if (ret)
		return ret;
	if (pos_in + len > i_size_read(inode_in))
		return -EINVAL;
	ret = mnt_want_write_file(file_out);
	if (ret)
		return ret;
	ret = file_in->f_op->clone_file_range(file_in, pos_in,
			file_out, pos_out, len);
	if (!ret) {
		fsnotify_access(file_in);
		fsnotify_modify(file_out);
	}
	mnt_drop_write_file(file_out);
	return ret;
}

static int vidioc_try_fmt_out(struct file *file, void *priv, struct v4l2_format *fmt)
{
	struct v4l2_loopback_device *dev;
	MARK();
	dev = v4l2loopback_getdevice(file);
	if (dev->ready_for_capture) {
		fmt->fmt.pix = dev->pix_format;
	} else {
		__u32 w = fmt->fmt.pix.width;
		__u32 h = fmt->fmt.pix.height;
		__u32 pixfmt = fmt->fmt.pix.pixelformat;
		const struct v4l2l_format *format = format_by_fourcc(pixfmt);
		if (w > max_width)
			w = max_width;
		if (h > max_height)
			h = max_height;
		dprintk("trying image %dx%d\n", w, h);
		if (w < 1)
			w = V4L2LOOPBACK_SIZE_DEFAULT_WIDTH;
		if (h < 1)
			h = V4L2LOOPBACK_SIZE_DEFAULT_HEIGHT;
		if (NULL == format)
			format = &formats[0];
		pix_format_set_size(&fmt->fmt.pix, format, w, h);
		fmt->fmt.pix.pixelformat = format->fourcc;
		fmt->fmt.pix.colorspace = V4L2_COLORSPACE_SRGB;
		if (V4L2_FIELD_ANY == fmt->fmt.pix.field)
			fmt->fmt.pix.field = V4L2_FIELD_NONE;
		dev->pix_format = fmt->fmt.pix;
	}
	return 0;
}

static int __init lp_setup (char *str)
{
	static int parport_ptr;
	int x;
	if (get_option(&str, &x)) {
		if (x == 0) {
			parport_nr[0] = LP_PARPORT_OFF;
		} else {
			printk(KERN_WARNING "warning: 'lp=0x%x' is deprecated, ignored\n", x);
			return 0;
		}
	} else if (!strncmp(str, "parport", 7)) {
		int n = simple_strtoul(str+7, NULL, 10);
		if (parport_ptr < LP_NO)
			parport_nr[parport_ptr++] = n;
		else
			printk(KERN_INFO "lp: too many ports, %s ignored.\n",
			       str);
	} else if (!strcmp(str, "auto")) {
		parport_nr[0] = LP_PARPORT_AUTO;
	} else if (!strcmp(str, "none")) {
		parport_nr[parport_ptr++] = LP_PARPORT_NONE;
	} else if (!strcmp(str, "reset")) {
		reset = 1;
	}
	return 1;
}

