void Compute(OpKernelContext* context) override {
  const Tensor& filter = context->input(1);
  const TensorShape& filter_shape = filter.shape();
  const Tensor& out_backprop = context->input(2);
  const TensorShape& out_backprop_shape = out_backprop.shape();
  TensorShape input_shape;
  if (takes_shape_) {
    const Tensor& input_sizes = context->input(0);
    OP_REQUIRES_OK(context, tensor::MakeShape(input_sizes, &input_shape));
  } else {
    input_shape = context->input(0).shape();
  }
  OP_REQUIRES(
      context, input_shape.dim_size(4) == filter_shape.dim_size(3),
      errors::InvalidArgument("input and filter_sizes must have the same "
                              "number of channels. Got ",
                              input_shape.dim_size(4), " for input and ",
                              filter_shape.dim_size(3), " for filter_sizes"));
  OP_REQUIRES(
      context, out_backprop_shape.dim_size(4) == filter_shape.dim_size(4),
      errors::InvalidArgument("out_backprop and filter_sizes must have the "
                              "same number of channels. Got ",
                              out_backprop_shape.dim_size(4),
                              " for out_backprop and ",
                              filter_shape.dim_size(4), " for filter_sizes"));
  ConvBackpropDimensions dims;
  OP_REQUIRES_OK(context, ConvBackpropComputeDimensions(
                              "Conv3DBackpropInputOp", 3,
                              input_shape, filter_shape, out_backprop_shape,
                              stride_, padding_, data_format_, &dims));
  Tensor* in_backprop;
  OP_REQUIRES_OK(context,
                 context->allocate_output(0, input_shape, &in_backprop));
  functor::CuboidConvolutionBackwardInput<Device, T>()(
      context->eigen_device<Device>(),
      in_backprop->tensor<T, 5>(),                     
      filter.tensor<T, 5>(),                           
      out_backprop.tensor<T, 5>(),                     
      static_cast<int>(dims.spatial_dims[0].stride),   
      static_cast<int>(dims.spatial_dims[1].stride),   
      static_cast<int>(dims.spatial_dims[2].stride));  
}

void *TrustedPrimitives::UntrustedLocalAlloc(size_t size) noexcept {
  void *result;
  CHECK_OCALL(
      ocall_untrusted_local_alloc(&result, static_cast<uint64_t>(size)));
  if (result && !IsOutsideEnclave(result, static_cast<uint64_t>(size))) {
    TrustedPrimitives::BestEffortAbort(
        "Allocated memory not found to be outside the enclave.");
  }
  if (!result) {
    errno = ENOMEM;
    TrustedPrimitives::DebugPuts("UntrustedLocalAlloc on SGX failed.");
  }
  return result;
}

int tipc_nl_node_dump_monitor_peer(struct sk_buff *skb,
				   struct netlink_callback *cb)
{
	struct net *net = sock_net(skb->sk);
	u32 prev_node = cb->args[1];
	u32 bearer_id = cb->args[2];
	int done = cb->args[0];
	struct tipc_nl_msg msg;
	int err;
	if (!prev_node) {
		struct nlattr **attrs = genl_dumpit_info(cb)->attrs;
		struct nlattr *mon[TIPC_NLA_MON_MAX + 1];
		if (!attrs[TIPC_NLA_MON])
			return -EINVAL;
		err = nla_parse_nested_deprecated(mon, TIPC_NLA_MON_MAX,
						  attrs[TIPC_NLA_MON],
						  tipc_nl_monitor_policy,
						  NULL);
		if (err)
			return err;
		if (!mon[TIPC_NLA_MON_REF])
			return -EINVAL;
		bearer_id = nla_get_u32(mon[TIPC_NLA_MON_REF]);
		if (bearer_id >= MAX_BEARERS)
			return -EINVAL;
	}
	if (done)
		return 0;
	msg.skb = skb;
	msg.portid = NETLINK_CB(cb->skb).portid;
	msg.seq = cb->nlh->nlmsg_seq;
	rtnl_lock();
	err = tipc_nl_add_monitor_peer(net, &msg, bearer_id, &prev_node);
	if (!err)
		done = 1;
	rtnl_unlock();
	cb->args[0] = done;
	cb->args[1] = prev_node;
	cb->args[2] = bearer_id;
	return skb->len;
}

assegment_append_asns (struct assegment *seg, as_t *asnos, int num)
{
