		      ASSEGMENT_DATA_SIZE (seg->length + num, 1));
  if (newas)
    {
      seg->as = newas;
      memcpy (seg->as + seg->length, asnos, ASSEGMENT_DATA_SIZE(num, 1));
      seg->length += num;
