{
  if(d_pos + len > d_content.size())
    throw std::out_of_range("Attempt to copy outside of packet");
  memcpy(dest, &d_content.at(d_pos), len);
  d_pos+=len;
}

bool red_stream_write_all(RedStream *stream, const void *in_buf, size_t n)
{
    const uint8_t *buf = (uint8_t *)in_buf;
    while (n) {
        int now = red_stream_write(stream, buf, n);
        if (now <= 0) {
            if (now == -1 && (errno == EINTR || errno == EAGAIN)) {
                continue;
            }
            return false;
        }
        n -= now;
        buf += now;
    }
    return true;
}

