format_fractional_part_nsecs(gchar *buf, size_t buflen, guint32 nsecs, const char *decimal_point, int precision)
{
	gsize decimal_point_len;
	gint8 num_buf[CHARS_NANOSECONDS];
	gint8 *num_end = &num_buf[CHARS_NANOSECONDS];
	gint8 *num_ptr;
	size_t num_len;
	ws_assert(precision != 0);
	if (buflen == 0) {
		return;
	}
	decimal_point_len = g_strlcpy(buf, decimal_point, buflen);
	if (decimal_point_len >= buflen) {
		return;
	}
	buf += decimal_point_len;
	buflen -= decimal_point_len;
	switch (precision) {
	case 1:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 100000000, precision);
		break;
	case 2:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 10000000, precision);
		break;
	case 3:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 1000000, precision);
		break;
	case 4:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 100000, precision);
		break;
	case 5:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 10000, precision);
		break;
	case 6:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 1000, precision);
		break;
	case 7:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 100, precision);
		break;
	case 8:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 10, precision);
		break;
	case 9:
		num_ptr = uint_to_str_back_len(num_end, nsecs,
		    precision);
		break;
	default:
		ws_assert_not_reached();
		break;
	}
	num_len = MIN((size_t)(num_end - num_ptr), buflen - 1);
	if (num_len == 0) {
		return;
	}
	memcpy(buf, num_ptr, num_len);
	*(buf + num_len) = '\0';
}

void M_LoadDefaults (void)
{
    int		i;
    int		len;
    FILE*	f;
    char	def[80];
    char	strparm[100];
    char*	newstring;
    int		parm;
    boolean	isstring;
    numdefaults = sizeof(defaults)/sizeof(defaults[0]);
    for (i=0 ; i<numdefaults ; i++)
	*defaults[i].location = defaults[i].defaultvalue;
    i = M_CheckParm ("-config");
    if (i && i<myargc-1)
    {
	defaultfile = myargv[i+1];
	printf ("	default file: %s\n",defaultfile);
    }
    else
	defaultfile = basedefault;
    f = fopen (defaultfile, "r");
    if (f)
    {
	while (!feof(f))
	{
	    isstring = false;
	    if (fscanf (f, "%79s %[^\n]\n", def, strparm) == 2)
	    {
		if (strparm[0] == '"')
		{
		    isstring = true;
		    len = strlen(strparm);
		    newstring = (char *) malloc(len);
		    strparm[len-1] = 0;
		    strcpy(newstring, strparm+1);
		}
		else if (strparm[0] == '0' && strparm[1] == 'x')
		    sscanf(strparm+2, "%x", &parm);
		else
		    sscanf(strparm, "%i", &parm);
		for (i=0 ; i<numdefaults ; i++)
		    if (!strcmp(def, defaults[i].name))
		    {
			if (!isstring)
			    *defaults[i].location = parm;
			else
			    *defaults[i].location =
				(int) newstring;
			break;
		    }
	    }
	}
	fclose (f);
    }
    for (i = 0; i < numdefaults; i++)
    {
        if (defaults[i].scantranslate)
        {
            parm = *defaults[i].location;
            defaults[i].untranslated = parm;
            *defaults[i].location = scantokey[parm];
        }
    }
}

int32_t *enc_untrusted_create_wait_queue() {
  MessageWriter input;
  MessageReader output;
  input.Push<uint64_t>(sizeof(int32_t));
  const auto status = NonSystemCallDispatcher(
      ::asylo::host_call::kLocalLifetimeAllocHandler, &input, &output);
  CheckStatusAndParamCount(status, output, "enc_untrusted_create_wait_queue",
                           2);
  int32_t *queue = reinterpret_cast<int32_t *>(output.next<uintptr_t>());
  int klinux_errno = output.next<int>();
  if (queue == nullptr) {
    errno = FromkLinuxErrorNumber(klinux_errno);
  }
  enc_untrusted_disable_waiting(queue);
  return queue;
}

