int lxc_safe_long(const char *numstr, long int *converted)
{
	char *err = NULL;
	signed long int sli;
	errno = 0;
	sli = strtol(numstr, &err, 0);
	if (errno == ERANGE && (sli == LONG_MAX || sli == LONG_MIN))
		return -ERANGE;
	if (errno != 0 && sli == 0)
		return -EINVAL;
	if (err == numstr || *err != '\0')
		return -EINVAL;
	*converted = sli;
	return 0;
}

check_tty(const char *tty)
{
	if ((tty == NULL) || (strlen(tty) == 0)) {
		return NULL;
	}
	if (strchr(tty, '/') != NULL) {
		if (strncmp(tty, "/dev/", 5) != 0) {
			return NULL;
		}
		tty = strrchr(tty, '/') + 1;
	}
	if (strlen(tty) == 0) {
		return NULL;
	}
	return tty;
}

