    }
    if (!(dwFlags & cmsFLAGS_NODEFAULTRESOURCEDEF)) {
        _cmsIOPrintf(mem, "%%%%EndResource\n");
        _cmsIOPrintf(mem, "\n%% CRD End\n");
    }
    dwBytesUsed = mem ->UsedSpace;
    return dwBytesUsed;
    cmsUNUSED_PARAMETER(ContextID);
}

SWFInput_readSBits(SWFInput input, int number)
 {
