static void imap_parser_save_arg(struct imap_parser *parser,
				 const unsigned char *data, size_t size)
{
	struct imap_arg *arg;
	char *str;
	arg = imap_arg_create(parser);
	switch (parser->cur_type) {
	case ARG_PARSE_ATOM:
	case ARG_PARSE_TEXT:
		if (size == 3 && i_memcasecmp(data, "NIL", 3) == 0) {
			arg->type = IMAP_ARG_NIL;
		} else {
			arg->type = IMAP_ARG_ATOM;
		}
		arg->_data.str = imap_parser_strdup(parser, data, size);
		arg->str_len = size;
		break;
	case ARG_PARSE_STRING:
		i_assert(size > 0);
		arg->type = IMAP_ARG_STRING;
		str = p_strndup(parser->pool, data+1, size-1);
		if (parser->str_first_escape >= 0 &&
		    (parser->flags & IMAP_PARSE_FLAG_NO_UNESCAPE) == 0) {
			(void)str_unescape(str + parser->str_first_escape-1);
		}
		arg->_data.str = str;
		arg->str_len = strlen(str);
		break;
	case ARG_PARSE_LITERAL_DATA:
		if ((parser->flags & IMAP_PARSE_FLAG_LITERAL_SIZE) != 0) {
			arg->type = parser->literal_nonsync ?
				IMAP_ARG_LITERAL_SIZE_NONSYNC :
				IMAP_ARG_LITERAL_SIZE;
			arg->_data.literal_size = parser->literal_size;
			arg->literal8 = parser->literal8;
			break;
		}
	case ARG_PARSE_LITERAL_DATA_FORCED:
		if ((parser->flags & IMAP_PARSE_FLAG_LITERAL_TYPE) != 0)
			arg->type = IMAP_ARG_LITERAL;
		else
			arg->type = IMAP_ARG_STRING;
		arg->_data.str = imap_parser_strdup(parser, data, size);
		arg->literal8 = parser->literal8;
		arg->str_len = size;
		break;
	default:
                i_unreached();
	}
	parser->cur_type = ARG_PARSE_NONE;
}

void IDNSpoofChecker::SetAllowedUnicodeSet(UErrorCode* status) {
  if (U_FAILURE(*status))
    return;
  const icu::UnicodeSet* recommended_set =
      uspoof_getRecommendedUnicodeSet(status);
  icu::UnicodeSet allowed_set;
  allowed_set.addAll(*recommended_set);
  const icu::UnicodeSet* inclusion_set = uspoof_getInclusionUnicodeSet(status);
  allowed_set.addAll(*inclusion_set);
  allowed_set.remove(0x338u);
  allowed_set.remove(0x58au);  
  allowed_set.remove(0x2010u);
  allowed_set.remove(0x2019u);  
  allowed_set.remove(0x2027u);
  allowed_set.remove(0x30a0u);  
   allowed_set.remove(0x2bbu);  
   allowed_set.remove(0x2bcu);  
#if defined(OS_MACOSX)
  allowed_set.remove(0x0620u);
  allowed_set.remove(0x0F8Cu);
  allowed_set.remove(0x0F8Du);
  allowed_set.remove(0x0F8Eu);
  allowed_set.remove(0x0F8Fu);
#endif
  allowed_set.remove(0x01CDu, 0x01DCu);  
  allowed_set.remove(0x1C80u, 0x1C8Fu);  
  allowed_set.remove(0x1E00u, 0x1E9Bu);  
  allowed_set.remove(0x1F00u, 0x1FFFu);  
  allowed_set.remove(0xA640u, 0xA69Fu);  
  allowed_set.remove(0xA720u, 0xA7FFu);  
  uspoof_setAllowedUnicodeSet(checker_, &allowed_set, status);
}

