void builtinFetchurl(const BasicDerivation & drv)
{
    auto url = drv.env.find("url");
    if (url == drv.env.end()) throw Error("attribute url missing");
    printMsg(lvlInfo, format("downloading %1%...") % url->second);
    auto data = downloadFile(url->second); 
    auto out = drv.env.find("out");
    if (out == drv.env.end()) throw Error("attribute url missing");
    writeFile(out->second, data.data);
    auto executable = drv.env.find("executable");
    if (executable != drv.env.end() && executable->second == "1") {
        if (chmod(out->second.c_str(), 0755) == -1)
            throw SysError(format("making %1% executable") % out->second);
    }
}

void Statement::Work_AfterPrepare(napi_env e, napi_status status, void* data) {
    std::unique_ptr<PrepareBaton> baton(static_cast<PrepareBaton*>(data));
    Statement* stmt = baton->stmt;
    Napi::Env env = stmt->Env();
    Napi::HandleScope scope(env);
    if (stmt->status != SQLITE_OK) {
        Error(baton.get());
        stmt->Finalize_();
    }
    else {
        stmt->prepared = true;
        if (!baton->callback.IsEmpty() && baton->callback.Value().IsFunction()) {
            Napi::Function cb = baton->callback.Value();
            Napi::Value argv[] = { env.Null() };
            TRY_CATCH_CALL(stmt->Value(), cb, 1, argv);
        }
    }
    STATEMENT_END();
}

