	struct pam_response *resp;
	if (ctx->flags & WINBIND_SILENT) {
		return PAM_SUCCESS;
	}
	pmsg[0] = &msg[0];
	msg[0].msg = discard_const_p(char, text);
	msg[0].msg_style = type;
	resp = NULL;
