			actual_tid, tid, next_tid(tid));
#endif
	stat(s, CMPXCHG_DOUBLE_CPU_FAIL);
}

static int _gnutls_verify_crl2(gnutls_x509_crl_t crl,
			       const gnutls_x509_crt_t * trusted_cas,
			       int tcas_size, unsigned int flags,
			       unsigned int *output)
{
    gnutls_datum_t crl_signed_data = { NULL, 0 };
    gnutls_datum_t crl_signature = { NULL, 0 };
    gnutls_x509_crt_t issuer;
    int ret, result;
    if (output)
	*output = 0;
    if (tcas_size >= 1)
	issuer = find_crl_issuer(crl, trusted_cas, tcas_size);
    else {
	gnutls_assert();
	if (output)
	    *output |= GNUTLS_CERT_SIGNER_NOT_FOUND | GNUTLS_CERT_INVALID;
	return 0;
    }
    if (issuer == NULL) {
	gnutls_assert();
	if (output)
	    *output |= GNUTLS_CERT_SIGNER_NOT_FOUND | GNUTLS_CERT_INVALID;
	return 0;
    }
    if (!(flags & GNUTLS_VERIFY_DISABLE_CA_SIGN)) {
	if (gnutls_x509_crt_get_ca_status(issuer, NULL) != 1) {
	    gnutls_assert();
	    if (output)
		*output |= GNUTLS_CERT_SIGNER_NOT_CA | GNUTLS_CERT_INVALID;
	    return 0;
	}
    }
    result =
	_gnutls_x509_get_signed_data(crl->crl, "tbsCertList",
				     &crl_signed_data);
    if (result < 0) {
	gnutls_assert();
	goto cleanup;
    }
    result =
	_gnutls_x509_get_signature(crl->crl, "signature", &crl_signature);
    if (result < 0) {
	gnutls_assert();
	goto cleanup;
    }
    ret =
	_gnutls_x509_verify_signature(&crl_signed_data, &crl_signature,
				      issuer);
    if (ret < 0) {
	gnutls_assert();
    } else if (ret == 0) {
	gnutls_assert();
	if (output)
	    *output |= GNUTLS_CERT_INVALID;
	ret = 0;
    }
    result = ret;
  cleanup:
    _gnutls_free_datum(&crl_signed_data);
    _gnutls_free_datum(&crl_signature);
    return result;
}

static int propagate_liveness(struct bpf_verifier_env *env,
			      const struct bpf_verifier_state *vstate,
			      struct bpf_verifier_state *vparent)
{
	struct bpf_reg_state *state_reg, *parent_reg;
	struct bpf_func_state *state, *parent;
	int i, frame, err = 0;
	if (vparent->curframe != vstate->curframe) {
		WARN(1, "propagate_live: parent frame %d current frame %d\n",
		     vparent->curframe, vstate->curframe);
		return -EFAULT;
	}
	BUILD_BUG_ON(BPF_REG_FP + 1 != MAX_BPF_REG);
	for (frame = 0; frame <= vstate->curframe; frame++) {
		parent = vparent->frame[frame];
		state = vstate->frame[frame];
		parent_reg = parent->regs;
		state_reg = state->regs;
		for (i = frame < vstate->curframe ? BPF_REG_6 : 0; i < BPF_REG_FP; i++) {
			err = propagate_liveness_reg(env, &state_reg[i],
						     &parent_reg[i]);
			if (err < 0)
				return err;
			if (err == REG_LIVE_READ64)
				mark_insn_zext(env, &parent_reg[i]);
		}
		for (i = 0; i < state->allocated_stack / BPF_REG_SIZE &&
			    i < parent->allocated_stack / BPF_REG_SIZE; i++) {
			parent_reg = &parent->stack[i].spilled_ptr;
			state_reg = &state->stack[i].spilled_ptr;
			err = propagate_liveness_reg(env, state_reg,
						     parent_reg);
			if (err < 0)
				return err;
		}
	}
	return 0;
}

void AXTree::PopulateOrderedSetItems(const AXNode* ordered_set,
                                     const AXNode* local_parent,
                                     std::vector<const AXNode*>& items,
                                     bool node_is_radio_button) const {
  if (!(ordered_set == local_parent)) {
    if (local_parent->data().role == ordered_set->data().role)
      return;
  }
   for (int i = 0; i < local_parent->child_count(); ++i) {
     const AXNode* child = local_parent->GetUnignoredChildAtIndex(i);
     if (node_is_radio_button &&
         child->data().role == ax::mojom::Role::kRadioButton)
      items.push_back(child);
    if (!node_is_radio_button && child->SetRoleMatchesItemRole(ordered_set))
      items.push_back(child);
    if (child->data().role == ax::mojom::Role::kGenericContainer ||
        child->data().role == ax::mojom::Role::kIgnored) {
      PopulateOrderedSetItems(ordered_set, child, items, node_is_radio_button);
    }
  }
}

