void Con_Dump_f( void ) {
	int l, x, i;
	short   *line;
	fileHandle_t f;
	int		bufferlen;
	char	*buffer;
	char	filename[MAX_QPATH];
	if ( Cmd_Argc() != 2 ) {
		Com_Printf( "usage: condump <filename>\n" );
		return;
	}
	Q_strncpyz( filename, Cmd_Argv( 1 ), sizeof( filename ) );
	COM_DefaultExtension( filename, sizeof( filename ), ".txt" );
	f = FS_FOpenFileWrite( filename );
	if ( !f ) {
		Com_Printf ("ERROR: couldn't open %s.\n", filename);
		return;
	}
	Com_Printf ("Dumped console text to %s.\n", filename );
	for ( l = con.current - con.totallines + 1 ; l <= con.current ; l++ )
	{
		line = con.text + ( l % con.totallines ) * con.linewidth;
		for ( x = 0 ; x < con.linewidth ; x++ )
			if ( ( line[x] & 0xff ) != ' ' ) {
				break;
			}
		if ( x != con.linewidth ) {
			break;
		}
	}
#ifdef _WIN32
	bufferlen = con.linewidth + 3 * sizeof ( char );
#else
	bufferlen = con.linewidth + 2 * sizeof ( char );
#endif
	buffer = Hunk_AllocateTempMemory( bufferlen );
	buffer[bufferlen-1] = 0;
	for ( ; l <= con.current ; l++ )
	{
		line = con.text + ( l % con.totallines ) * con.linewidth;
		for ( i = 0; i < con.linewidth; i++ )
			buffer[i] = line[i] & 0xff;
		for ( x = con.linewidth - 1 ; x >= 0 ; x-- )
		{
			if ( buffer[x] == ' ' ) {
				buffer[x] = 0;
			} else {
				break;
			}
		}
#ifdef _WIN32
		Q_strcat(buffer, bufferlen, "\r\n");
#else
		Q_strcat(buffer, bufferlen, "\n");
#endif
		FS_Write( buffer, strlen( buffer ), f );
	}
	Hunk_FreeTempMemory( buffer );
	FS_FCloseFile( f );
}

void connection_graceful_shutdown_maint (server *srv) {
    connections * const conns = &srv->conns;
    const int graceful_expire =
      (srv->graceful_expire_ts && srv->graceful_expire_ts < log_epoch_secs);
    for (uint32_t ndx = 0; ndx < conns->used; ++ndx) {
        connection * const con = conns->ptr[ndx];
        int changed = 0;
        request_st * const r = &con->request;
        if (r->state == CON_STATE_CLOSE) {
            if (HTTP_LINGER_TIMEOUT > 1)
                con->close_timeout_ts -= (HTTP_LINGER_TIMEOUT - 1);
            if (log_epoch_secs - con->close_timeout_ts > HTTP_LINGER_TIMEOUT)
                changed = 1;
        }
        else if (con->h2 && r->state == CON_STATE_WRITE) {
            h2_send_goaway(con, H2_E_NO_ERROR);
            if (0 == con->h2->rused && chunkqueue_is_empty(con->write_queue)) {
                connection_set_state(r, CON_STATE_RESPONSE_END);
                changed = 1;
            }
        }
        else if (r->state == CON_STATE_READ && con->request_count > 1
                 && chunkqueue_is_empty(con->read_queue)) {
            connection_set_state_error(r, CON_STATE_ERROR);
            changed = 1;
        }
        if (graceful_expire) {
            connection_set_state_error(r, CON_STATE_ERROR);
            changed = 1;
        }
        r->keep_alive = 0;            
        r->conf.bytes_per_second = 0;         
        r->conf.global_bytes_per_second = 0;  
        if (con->traffic_limit_reached) {
            con->traffic_limit_reached = 0;
            changed = 1;
        }
        if (changed) {
            connection_state_machine(con);
        }
    }
}

char *M_fs_path_join_parts(const M_list_str_t *path, M_fs_system_t sys_type)
{
	M_list_str_t *parts;
	const char   *part;
	char         *out;
	size_t        len;
	size_t        i;
	size_t        count;
	if (path == NULL) {
		return NULL;
	}
	len = M_list_str_len(path);
	if (len == 0) {
		return NULL;
	}
	sys_type = M_fs_path_get_system_type(sys_type);
	parts = M_list_str_duplicate(path);
	for (i=len-1; i>0; i--) {
		part = M_list_str_at(parts, i);
		if (part == NULL || *part == '\0') {
			M_list_str_remove_at(parts, i);
		}
	}
	len = M_list_str_len(parts);
	part = M_list_str_at(parts, 0);
	if (len == 1 && (part == NULL || *part == '\0')) {
		M_list_str_destroy(parts);
		if (sys_type == M_FS_SYSTEM_WINDOWS) {
			return M_strdup("\\\\");
		}
		return M_strdup("/");
	}
	if (sys_type == M_FS_SYSTEM_WINDOWS && len > 0) {
		part  = M_list_str_at(parts, 0);
		count = (len == 1) ? 2 : 1;
		if (part != NULL && *part == '\0') {
			for (i=0; i<count; i++) {
				M_list_str_insert_at(parts, "", 0);
			}
		} else if (M_fs_path_isabs(part, sys_type) && len == 1) {
			M_list_str_insert_at(parts, "", 1);
		}
	}
	out = M_list_str_join(parts, (unsigned char)M_fs_path_get_system_sep(sys_type));
	M_list_str_destroy(parts);
	return out;
}

String_Obj Parser::parse_ie_property()
{
  lex< ie_property >();
  Token str(lexed);
  const char* i = str.begin;
  const char* p = find_first_in_interval< exactly<hash_lbrace>, block_comment >(str.begin, str.end);
  if (!p) {
    return SASS_MEMORY_NEW(String_Quoted, pstate, std::string(str.begin, str.end));
  }
  String_Schema* schema = SASS_MEMORY_NEW(String_Schema, pstate);
  while (i < str.end) {
    p = find_first_in_interval< exactly<hash_lbrace>, block_comment >(i, str.end);
    if (p) {
      if (i < p) {
        schema->append(SASS_MEMORY_NEW(String_Constant, pstate, std::string(i, p))); 
      }
      if (peek < sequence < optional_spaces, exactly<rbrace> > >(p+2)) { position = p+2;
        css_error("Invalid CSS", " after ", ": expected expression (e.g. 1px, bold), was ");
      }
      const char* j = skip_over_scopes< exactly<hash_lbrace>, exactly<rbrace> >(p+2, str.end); 
      if (j) {
        Expression_Obj interp_node = Parser::from_token(Token(p+2, j), ctx, traces, pstate, source).parse_list();
        interp_node->is_interpolant(true);
        schema->append(interp_node);
        i = j;
      }
      else {
        error("unterminated interpolant inside IE function " + str.to_string());
      }
    }
    else { 
      if (i < str.end) {
        schema->append(SASS_MEMORY_NEW(String_Constant, pstate, std::string(i, str.end)));
      }
      break;
    }
  }
  return schema;
}

