htp_param_t *htp_tx_req_get_param_ex(htp_tx_t *tx, enum htp_data_source_t source, const char *name, size_t name_len) {
    if ((tx == NULL) || (name == NULL)) return NULL;
    htp_param_t *p = NULL;
    for (size_t i = 0, n = htp_table_size(tx->request_params); i < n; i++) {
        p = htp_table_get_index(tx->request_params, i, NULL);
        if (p->source != source) continue;
        if (bstr_cmp_mem_nocase(p->name, name, name_len) == 0) return p;
    }
    return NULL;
}

get_cdtext_generic (void *p_user_data)
{
  generic_img_private_t *p_env = p_user_data;
  uint8_t *p_cdtext_data = NULL;
  size_t  len;
  if (!p_env) return NULL;
  if (p_env->b_cdtext_error) return NULL;
  if (NULL == p_env->cdtext) {
    p_cdtext_data = read_cdtext_generic (p_env);
    if (NULL != p_cdtext_data) {
      len = CDIO_MMC_GET_LEN16(p_cdtext_data)-2;
      p_env->cdtext = cdtext_init();
      if(len <= 0 || 0 != cdtext_data_init (p_env->cdtext, &p_cdtext_data[4], len)) {
        p_env->b_cdtext_error = true;
        free(p_env->cdtext);
        p_env->cdtext = NULL;
      }
      free(p_cdtext_data);
    }
  }
  return p_env->cdtext;
}

