mm_skey_query(void *ctx, char **name, char **infotxt,
   u_int *numprompts, char ***prompts, u_int **echo_on)
{
	Buffer m;
	int len;
	u_int success;
	char *p, *challenge;
	debug3("%s: entering", __func__);
	buffer_init(&m);
	mm_request_send(pmonitor->m_recvfd, MONITOR_REQ_SKEYQUERY, &m);
	mm_request_receive_expect(pmonitor->m_recvfd, MONITOR_ANS_SKEYQUERY,
	    &m);
	success = buffer_get_int(&m);
	if (success == 0) {
		debug3("%s: no challenge", __func__);
		buffer_free(&m);
		return (-1);
	}
	challenge  = buffer_get_string(&m, NULL);
	buffer_free(&m);
	debug3("%s: received challenge: %s", __func__, challenge);
	mm_chall_setup(name, infotxt, numprompts, prompts, echo_on);
	len = strlen(challenge) + strlen(SKEY_PROMPT) + 1;
	p = xmalloc(len);
	strlcpy(p, challenge, len);
	strlcat(p, SKEY_PROMPT, len);
	(*prompts)[0] = p;
	xfree(challenge);
	return (0);
}

char* _multi_string_alloc_and_copy( LPCWSTR in )
{
    char *chr;
    int len = 0;
    if ( !in )
    {
        return NULL;
    }
    while ( in[ len ] != 0 || in[ len + 1 ] != 0 )
    {
        len ++;
    }
    chr = malloc( len + 2 );
    len = 0;
    while ( in[ len ] != 0 || in[ len + 1 ] != 0 )
    {
        chr[ len ] = 0xFF & in[ len ];
        len ++;
    }
    chr[ len ++ ] = '\0';
    chr[ len ++ ] = '\0';
    return chr;
}

 void * calloc(size_t n, size_t lb)
 {
    if (lb && n > GC_SIZE_MAX / lb)
       return NULL;
 #   if defined(GC_LINUX_THREADS) 
        {
          static GC_bool lib_bounds_set = FALSE;
          ptr_t caller = (ptr_t)__builtin_return_address(0);
          if (!EXPECT(lib_bounds_set, TRUE)) {
            GC_init_lib_bounds();
            lib_bounds_set = TRUE;
          }
          if (((word)caller >= (word)GC_libpthread_start
               && (word)caller < (word)GC_libpthread_end)
              || ((word)caller >= (word)GC_libld_start
                  && (word)caller < (word)GC_libld_end))
            return GC_malloc_uncollectable(n*lb);
        }
#   endif
    return((void *)REDIRECT_MALLOC(n*lb));
}

static const char* jvp_literal_number_literal(jv n) {
  assert(JVP_HAS_FLAGS(n, JVP_FLAGS_NUMBER_LITERAL));
  decNumber *pdec = jvp_dec_number_ptr(n);
  jvp_literal_number* plit = jvp_literal_number_ptr(n);
  if (decNumberIsNaN(pdec)) {
    return "null";
  }
  if (decNumberIsInfinite(pdec)) {
    return NULL;
  }
  if (plit->literal_data == NULL) {
    int len = jvp_dec_number_ptr(n)->digits + 14;
    plit->literal_data = jv_mem_alloc(len);
    decNumberToString(pdec, plit->literal_data);
  }
  return plit->literal_data;
}

static int allocDynamic(dynamicPtr *dp, int initialSize, void *data)
{
	if(data == NULL) {
		dp->logicalSize = 0;
		dp->dataGood = FALSE;
		dp->data = gdMalloc(initialSize);
	} else {
		dp->logicalSize = initialSize;
		dp->dataGood = TRUE;
		dp->data = data;
	}
	if(dp->data != NULL) {
		dp->realSize = initialSize;
		dp->dataGood = TRUE;
		dp->pos = 0;
		return TRUE;
	} else {
		dp->realSize = 0;
		return FALSE;
	}
}

void gnrc_sixlowpan_iphc_send(gnrc_pktsnip_t *pkt, void *ctx, unsigned page)
{
    gnrc_netif_hdr_t *netif_hdr = pkt->data;
    gnrc_netif_t *netif = gnrc_netif_hdr_get_netif(netif_hdr);
    gnrc_pktsnip_t *tmp;
    size_t orig_datagram_size = gnrc_pkt_len(pkt->next);
    ipv6_hdr_t *ipv6_hdr = pkt->next->data;
    ipv6_addr_t dst;
    if (IS_USED(MODULE_GNRC_SIXLOWPAN_FRAG_MINFWD)) {
        dst = ipv6_hdr->dst;    
    }
    if ((tmp = _iphc_encode(pkt, pkt->data, netif))) {
        if (IS_USED(MODULE_GNRC_SIXLOWPAN_FRAG_MINFWD) && (ctx != NULL) &&
            (gnrc_sixlowpan_frag_minfwd_frag_iphc(tmp, orig_datagram_size, &dst,
                                                  ctx) == 0)) {
            DEBUG("6lo iphc minfwd: putting slack in first fragment\n");
            return;
        }
        gnrc_sixlowpan_multiplex_by_size(tmp, orig_datagram_size, netif, page);
    }
    else {
        if (IS_USED(MODULE_GNRC_SIXLOWPAN_FRAG_MINFWD)) {
            gnrc_sixlowpan_frag_fb_t *fb = ctx;
            if (fb->pkt == pkt) {
                fb->pkt = NULL;
            }
        }
        gnrc_pktbuf_release(pkt);
    }
}

static void fill_model_structures(SPICE_GNUC_UNUSED Encoder *encoder, FamilyStat *family_stat,
                                  unsigned int rep_first, unsigned int first_size,
                                  unsigned int rep_next, unsigned int mul_size,
                                  unsigned int levels, unsigned int ncounters,
                                  unsigned int nbuckets, unsigned int n_buckets_ptrs)
{
    unsigned int
    bsize,
    bstart,
    bend = 0,
    repcntr,
    bnumber;
    COUNTER * free_counter = family_stat->counters;
    bnumber = 0;
    repcntr = rep_first + 1;    
    bsize = first_size;
    do { 
        if (bnumber) {
            bstart = bend + 1;
        } else {
            bstart = 0;
        }
        if (!--repcntr) {
            repcntr = rep_next;
            bsize *= mul_size;
        }
        bend = bstart + bsize - 1;
        if (bend + bsize >= levels) {
            bend = levels - 1;
        }
        family_stat->buckets_buf[bnumber].pcounters = free_counter;
        free_counter += ncounters;
        spice_assert(bstart < n_buckets_ptrs);
        {
            unsigned int i;
            spice_assert(bend < n_buckets_ptrs);
            for (i = bstart; i <= bend; i++) {
                family_stat->buckets_ptrs[i] = family_stat->buckets_buf + bnumber;
            }
        }
        bnumber++;
    } while (bend < levels - 1);
    spice_assert(free_counter - family_stat->counters == (ptrdiff_t)(nbuckets * ncounters));
}

