static int hardware_enable_all(void)
{
	int r = 0;
	raw_spin_lock(&kvm_lock);
	kvm_usage_count++;
	if (kvm_usage_count == 1) {
		atomic_set(&hardware_enable_failed, 0);
		on_each_cpu(hardware_enable_nolock, NULL, 1);
