	return r;
}

void Inspect::operator()(Complex_Selector_Ptr c)
{
  Compound_Selector_Obj      head = c->head();
  Complex_Selector_Obj            tail = c->tail();
  Complex_Selector::Combinator comb = c->combinator();
  if (comb == Complex_Selector::ANCESTOR_OF && (!head || head->empty())) {
    if (tail) tail->perform(this);
    return;
  }
  if (c->has_line_feed()) {
    if (!(c->has_parent_ref())) {
      append_optional_linefeed();
      append_indentation();
    }
  }
  if (head && head->length() != 0) head->perform(this);
  bool is_empty = !head || head->length() == 0 || head->is_empty_reference();
  bool is_tail = head && !head->is_empty_reference() && tail;
  if (output_style() == COMPRESSED && comb != Complex_Selector::ANCESTOR_OF) scheduled_space = 0;
  switch (comb) {
    case Complex_Selector::ANCESTOR_OF:
      if (is_tail) append_mandatory_space();
    break;
    case Complex_Selector::PARENT_OF:
      append_optional_space();
      append_string(">");
      append_optional_space();
    break;
    case Complex_Selector::ADJACENT_TO:
      append_optional_space();
      append_string("+");
      append_optional_space();
    break;
    case Complex_Selector::REFERENCE:
      append_mandatory_space();
      append_string("/");
      c->reference()->perform(this);
      append_string("/");
      append_mandatory_space();
    break;
    case Complex_Selector::PRECEDES:
      if (is_empty) append_optional_space();
      else append_mandatory_space();
      append_string("~");
      if (tail) append_mandatory_space();
      else append_optional_space();
    break;
    default: break;
  }
  if (tail && comb != Complex_Selector::ANCESTOR_OF) {
    if (c->has_line_break()) append_optional_linefeed();
  }
  if (tail) tail->perform(this);
  if (!tail && c->has_line_break()) {
    if (output_style() == COMPACT) {
      append_mandatory_space();
    }
  }
}

void SSH_Access::LogSSHMessage()
{
   const char *b;
   int s;
   pty_recv_buf->Get(&b,&s);
   const char *eol=find_char(b,s,'\n');
   if(!eol)
   {
      if(pty_recv_buf->Eof())
      {
	 if(s>0)
	    LogRecv(4,b);
	 LogError(0,_("Peer closed connection"));
      }
      if(pty_recv_buf->Error())
	 LogError(4,"pty read: %s",pty_recv_buf->ErrorText());
      if(pty_recv_buf->Eof() || pty_recv_buf->Error()) {
	 if(last_ssh_message && time_t(now)-last_ssh_message_time<4)
	    LogError(0,"%s",last_ssh_message.get());
	 Disconnect(last_ssh_message);
      }
      return;
   }
   s=eol-b+1;
   int chomp_cr=(s>=2 && b[s-2]=='\r');
   last_ssh_message.nset(b,s-1-chomp_cr);
   last_ssh_message_time=now;
   pty_recv_buf->Skip(s);
   LogRecv(4,last_ssh_message);
   if(last_ssh_message.begins_with("ssh: "))
      last_ssh_message.set(last_ssh_message+5);
   if(!received_greeting && last_ssh_message.eq(greeting))
      received_greeting=true;
}

