}

header_match(uschar *name, BOOL has_addresses, BOOL cond, string_item *strings,
  int count, ...)
{
va_list ap;
string_item *s;
int i;
int slen = Ustrlen(name);
for (s = strings; s != NULL; s = s->next)
  {
  if (one_pattern_match(name, slen, has_addresses, s->text)) return cond;
  }
va_start(ap, count);
for (i = 0; i < count; i++)
  if (one_pattern_match(name, slen, has_addresses, va_arg(ap, uschar *)))
    {
    va_end(ap);
    return cond;
    }
va_end(ap);
return !cond;
}

set_range(bitstr_t *bits, int low, int high, int start, int stop, int step) {
	Debug(DPARS|DEXT, ("set_range(?,%d,%d,%d,%d,%d)\n",
			   low, high, start, stop, step))
	if (start < low || stop > high)
		return (EOF);
	start -= low;
	stop -= low;
	if (step == 1) {
		bit_nset(bits, start, stop);
	} else {
		for (int i = start; i <= stop; i += step)
			bit_set(bits, i);
	}
	return (OK);
}

struct page *vm_normal_page(struct vm_area_struct *vma, unsigned long addr,
				pte_t pte)
{
	unsigned long pfn;
	if (HAVE_PTE_SPECIAL) {
		if (likely(!pte_special(pte))) {
			VM_BUG_ON(!pfn_valid(pte_pfn(pte)));
			return pte_page(pte);
		}
		VM_BUG_ON(!(vma->vm_flags & (VM_PFNMAP | VM_MIXEDMAP)));
		return NULL;
	}
	pfn = pte_pfn(pte);
	if (unlikely(vma->vm_flags & (VM_PFNMAP|VM_MIXEDMAP))) {
		if (vma->vm_flags & VM_MIXEDMAP) {
			if (!pfn_valid(pfn))
				return NULL;
			goto out;
		} else {
			unsigned long off;
			off = (addr - vma->vm_start) >> PAGE_SHIFT;
			if (pfn == vma->vm_pgoff + off)
				return NULL;
			if (!is_cow_mapping(vma->vm_flags))
				return NULL;
		}
	}
	VM_BUG_ON(!pfn_valid(pfn));
out:
	return pfn_to_page(pfn);
}

