static unsigned int help(struct sk_buff *skb,
			 enum ip_conntrack_info ctinfo,
			 unsigned int protoff,
			 unsigned int matchoff,
			 unsigned int matchlen,
			 struct nf_conntrack_expect *exp)
{
	char buffer[sizeof("4294967296 65635")];
	u_int16_t port;
	unsigned int ret;
	exp->saved_proto.tcp.port = exp->tuple.dst.u.tcp.port;
	exp->dir = IP_CT_DIR_REPLY;
	exp->expectfn = nf_nat_follow_master;
	for (port = ntohs(exp->saved_proto.tcp.port); port != 0; port++) {
		int ret;
		exp->tuple.dst.u.tcp.port = htons(port);
		ret = nf_ct_expect_related(exp);
		if (ret == 0)
			break;
		else if (ret != -EBUSY) {
			port = 0;
			break;
		}
	}
	if (port == 0) {
		nf_ct_helper_log(skb, exp->master, "all ports in use");
		return NF_DROP;
	}
	ret = nf_nat_mangle_tcp_packet(skb, exp->master, ctinfo,
				       protoff, matchoff, matchlen, buffer,
				       strlen(buffer));
	if (ret != NF_ACCEPT) {
		nf_ct_helper_log(skb, exp->master, "cannot mangle packet");
		nf_ct_unexpect_related(exp);
	}
	return ret;
}

TfLiteStatus Eval(TfLiteContext* context, TfLiteNode* node) {
  const TfLiteTensor* input = GetInput(context, node, kInput);
  const TfLiteTensor* input_state = GetInput(context, node, kInputState);
  const TfLiteTensor* gate_weight = GetInput(context, node, kGateWeight);
  const TfLiteTensor* gate_bias = GetInput(context, node, kGateBias);
  const TfLiteTensor* candidate_weight =
      GetInput(context, node, kCandidateWeight);
  const TfLiteTensor* candidate_bias = GetInput(context, node, kCandidateBias);
  TfLiteTensor* output = GetOutput(context, node, kOutput);
  TfLiteTensor* output_state = GetOutput(context, node, kOutputState);
  TfLiteTensor* activation = GetTemporary(context, node, kActivation);
  TfLiteTensor* concat = GetTemporary(context, node, kConcat);
  auto cpu_backend_context = CpuBackendContext::GetFromContext(context);
  if (gate_weight->type == kTfLiteFloat32) {
    GruImpl(input, input_state, gate_weight, gate_bias, candidate_weight,
            candidate_bias, output, output_state, activation, concat,
            cpu_backend_context);
  } else {
    context->ReportError(context,
                         "Unsupported combination of data types for GruCell");
    return kTfLiteError;
  }
  return kTfLiteOk;
}

void Compute(OpKernelContext* ctx) override {
  const Tensor& indices = ctx->input(0);
  const auto values = ctx->input(1).flat<Tidx>();
  const Tensor& dense_shape = ctx->input(2);
  const Tensor& size_t = ctx->input(3);
  const auto weights = ctx->input(4).flat<T>();
  const int64_t weights_size = weights.size();
  Tidx size = size_t.scalar<Tidx>()();
  OP_REQUIRES(
      ctx, size >= 0,
      errors::InvalidArgument("size (", size, ") must be non-negative"));
  bool is_1d = dense_shape.NumElements() == 1;
  Tensor* out_t;
  functor::SetZeroFunctor<Device, T> fill;
  if (is_1d) {
    OP_REQUIRES_OK(ctx, ctx->allocate_output(0, TensorShape({size}), &out_t));
    auto out = out_t->flat<T>();
    fill(ctx->eigen_device<Device>(), out);
    if (binary_output_) {
      OP_REQUIRES_OK(ctx,
                     functor::BincountFunctor<Device, Tidx, T, true>::Compute(
                         ctx, values, weights, out, size));
    } else {
      OP_REQUIRES_OK(
          ctx, functor::BincountFunctor<Device, Tidx, T, false>::Compute(
                   ctx, values, weights, out, size));
    }
  } else {
    const auto shape = dense_shape.flat<int64_t>();
    const int64_t num_rows = shape(0);
    OP_REQUIRES_OK(
        ctx, ctx->allocate_output(0, TensorShape({num_rows, size}), &out_t));
    const auto out = out_t->matrix<T>();
    fill(ctx->eigen_device<Device>(), out_t->flat<T>());
    const auto indices_mat = indices.matrix<int64_t>();
    for (int64_t i = 0; i < indices_mat.dimension(0); ++i) {
      const int64_t batch = indices_mat(i, 0);
      const Tidx bin = values(i);
      if (bin < size) {
        if (binary_output_) {
          out(batch, bin) = T(1);
        } else {
          if (weights_size) {
            out(batch, bin) += weights(i);
          } else {
            out(batch, bin) += T(1);
          }
        }
      }
    }
  }
}

 static size_t consume_init_expr (ut8 *buf, ut8 *max, ut8 eoc, void *out, ut32 *offset) {
 	ut32 i = 0;
 	while (buf + i < max && buf[i] != eoc) {
		i += 1;
 	}
 	if (buf[i] != eoc) {
 		return 0;
	}
	if (offset) {
		*offset += i + 1;
	}
	return i + 1;
}

