bool copyaudiodata (AFfilehandle infile, AFfilehandle outfile, int trackid)
{
	int frameSize = afGetVirtualFrameSize(infile, trackid, 1);
	const int kBufferFrameCount = 65536;
	void *buffer = malloc(kBufferFrameCount * frameSize);
	AFframecount totalFrames = afGetFrameCount(infile, AF_DEFAULT_TRACK);
	AFframecount totalFramesWritten = 0;
	bool success = true;
	while (totalFramesWritten < totalFrames)
	{
		AFframecount framesToRead = totalFrames - totalFramesWritten;
		if (framesToRead > kBufferFrameCount)
			framesToRead = kBufferFrameCount;
		AFframecount framesRead = afReadFrames(infile, trackid, buffer,
			framesToRead);
		if (framesRead < framesToRead)
		{
			fprintf(stderr, "Bad read of audio track data.\n");
			success = false;
			break;
		}
		AFframecount framesWritten = afWriteFrames(outfile, trackid, buffer,
			framesRead);
		if (framesWritten < framesRead)
		{
			fprintf(stderr, "Bad write of audio track data.\n");
			success = false;
			break;
		}
		totalFramesWritten += framesWritten;
	}
	free(buffer);
	return success;
}

static int stszin(int size)
{
    int cnt;
    uint32_t ofs;
    u32in();
    u32in();
    mp4config.frame.ents = u32in();
    mp4config.frame.data = malloc(sizeof(*mp4config.frame.data)
                                  * (mp4config.frame.ents + 1));
    if (!mp4config.frame.data)
        return ERR_FAIL;
    ofs = 0;
    mp4config.frame.data[0] = ofs;
    for (cnt = 0; cnt < mp4config.frame.ents; cnt++)
    {
        uint32_t fsize = u32in();
        ofs += fsize;
        if (mp4config.frame.maxsize < fsize)
            mp4config.frame.maxsize = fsize;
        mp4config.frame.data[cnt + 1] = ofs;
        if (ofs < mp4config.frame.data[cnt])
            return ERR_FAIL;
    }
    return size;
}

static ssize_t usbip_sockfd_store(struct device *dev, struct device_attribute *attr,
			    const char *buf, size_t count)
{
	struct stub_device *sdev = dev_get_drvdata(dev);
	int sockfd = 0;
	struct socket *socket;
	int rv;
	if (!sdev) {
		dev_err(dev, "sdev is null\n");
		return -ENODEV;
	}
	rv = sscanf(buf, "%d", &sockfd);
	if (rv != 1)
		return -EINVAL;
	if (sockfd != -1) {
		int err;
		dev_info(dev, "stub up\n");
		spin_lock_irq(&sdev->ud.lock);
		if (sdev->ud.status != SDEV_ST_AVAILABLE) {
			dev_err(dev, "not ready\n");
			goto err;
		}
		socket = sockfd_lookup(sockfd, &err);
		if (!socket) {
			dev_err(dev, "failed to lookup sock");
			goto err;
		}
		if (socket->type != SOCK_STREAM) {
			dev_err(dev, "Expecting SOCK_STREAM - found %d",
				socket->type);
			goto sock_err;
		}
		sdev->ud.tcp_socket = socket;
		sdev->ud.sockfd = sockfd;
		spin_unlock_irq(&sdev->ud.lock);
		sdev->ud.tcp_rx = kthread_get_run(stub_rx_loop, &sdev->ud,
						  "stub_rx");
		sdev->ud.tcp_tx = kthread_get_run(stub_tx_loop, &sdev->ud,
						  "stub_tx");
		spin_lock_irq(&sdev->ud.lock);
		sdev->ud.status = SDEV_ST_USED;
		spin_unlock_irq(&sdev->ud.lock);
	} else {
		dev_info(dev, "stub down\n");
		spin_lock_irq(&sdev->ud.lock);
		if (sdev->ud.status != SDEV_ST_USED)
			goto err;
		spin_unlock_irq(&sdev->ud.lock);
		usbip_event_add(&sdev->ud, SDEV_EVENT_DOWN);
	}
	return count;
sock_err:
	sockfd_put(socket);
err:
	spin_unlock_irq(&sdev->ud.lock);
	return -EINVAL;
}

join_read_last(JOIN_TAB *tab)
