static int adpt_queue_lck(struct scsi_cmnd *cmd)
{
	adpt_hba* pHba = NULL;
	struct adpt_device* pDev = NULL;	
	if ((cmd->cmnd[0] == REQUEST_SENSE) && (cmd->sense_buffer[0] != 0)) {
		cmd->result = (DID_OK << 16);
		scsi_done(cmd);
		return 0;
	}
	pHba = (adpt_hba*)cmd->device->host->hostdata[0];
	if (!pHba) {
		return FAILED;
	}
	rmb();
	if ((pHba->state) & DPTI_STATE_RESET)
		return SCSI_MLQUEUE_HOST_BUSY;
	if((pDev = (struct adpt_device*) (cmd->device->hostdata)) == NULL) {
		if ((pDev = adpt_find_device(pHba, (u32)cmd->device->channel, (u32)cmd->device->id, cmd->device->lun)) == NULL) {
			cmd->result = (DID_NO_CONNECT << 16);
			scsi_done(cmd);
			return 0;
		}
		cmd->device->hostdata = pDev;
	}
	pDev->pScsi_dev = cmd->device;
	if (pDev->state & DPTI_DEV_RESET ) {
		return FAILED;
	}
	return adpt_scsi_to_i2o(pHba, cmd, pDev);
}

int x509_get_name( unsigned char **p, const unsigned char *end,
                   x509_name *cur )
{
    int ret;
    size_t len;
    const unsigned char *end2;
    x509_name *use;
    if( ( ret = asn1_get_tag( p, end, &len,
            ASN1_CONSTRUCTED | ASN1_SET ) ) != 0 )
        return( POLARSSL_ERR_X509_INVALID_NAME + ret );
    end2 = end;
    end  = *p + len;
    use = cur;
    do
    {
        if( ( ret = x509_get_attr_type_value( p, end, use ) ) != 0 )
            return( ret );
        if( *p != end )
        {
            use->next = (x509_name *) polarssl_malloc(
                    sizeof( x509_name ) );
            if( use->next == NULL )
                return( POLARSSL_ERR_X509_MALLOC_FAILED );
            memset( use->next, 0, sizeof( x509_name ) );
            use = use->next;
        }
    }
    while( *p != end );
    if( *p == end2 )
        return( 0 );
    cur->next = (x509_name *) polarssl_malloc(
         sizeof( x509_name ) );
    if( cur->next == NULL )
        return( POLARSSL_ERR_X509_MALLOC_FAILED );
    memset( cur->next, 0, sizeof( x509_name ) );
    return( x509_get_name( p, end2, cur->next ) );
}

