static void _distance_scan(const unsigned int len,
                           const longT *const g,
                           longT (*const sep)(const longT, const longT, const longT *const),
                           longT (*const f)(const longT, const longT, const longT *const),
                           longT *const s,
                           longT *const t,
                           longT *const dt) {
  longT q = s[0] = t[0] = 0;
  for (int u = 1; u<(int)len; ++u) { 
    while ((q>=0) && f(t[q],s[q],g)>f(t[q],u,g)) { --q; }
    if (q<0) { q = 0; s[0] = u; }
    else { const longT w = 1 + sep(s[q], u, g); if (w<(longT)len) { ++q; s[q] = u; t[q] = w; }}
  }
  for (int u = (int)len - 1; u>=0; --u) { dt[u] = f(u,s[q],g); if (u==t[q]) --q; }

bool WideToCharMap(const wchar *Src,char *Dest,size_t DestSize,bool &Success)
{
  if (wcschr(Src,(wchar)MappedStringMark)==NULL)
    return false;
  Success=true;
  uint SrcPos=0,DestPos=0;
  while (Src[SrcPos]!=0 && DestPos<DestSize-MB_CUR_MAX)
  {
    if (uint(Src[SrcPos])==MappedStringMark)
    {
      SrcPos++;
      continue;
    }
    if (uint(Src[SrcPos])>=MapAreaStart+0x80 && uint(Src[SrcPos])<MapAreaStart+0x100)
      Dest[DestPos++]=char(uint(Src[SrcPos++])-MapAreaStart);
    else
    {
      mbstate_t ps;
      memset(&ps,0,sizeof(ps));
      if (wcrtomb(Dest+DestPos,Src[SrcPos],&ps)==-1)
      {
        Dest[DestPos]='_';
        Success=false;
      }
      SrcPos++;
      memset(&ps,0,sizeof(ps));
      int Length=mbrlen(Dest+DestPos,MB_CUR_MAX,&ps);
      DestPos+=Max(Length,1);
    }
  }
  Dest[Min(DestPos,DestSize-1)]=0;
  return true;
}

ResponsePtr Server::ServeStatic(RequestPtr request) {
