ospf_fifo_pop (struct ospf_fifo *fifo)
{
  struct ospf_packet *op;
  op = fifo->head;
  if (op)
    {
      fifo->head = op->next;
      if (fifo->head == NULL)
	fifo->tail = NULL;
      fifo->count--;
    }
  return op;
}

