void Compute(OpKernelContext* ctx) override {
  const Tensor& values_tensor = ctx->input(0);
  const Tensor& value_range_tensor = ctx->input(1);
  const Tensor& nbins_tensor = ctx->input(2);
  OP_REQUIRES(ctx, TensorShapeUtils::IsVector(value_range_tensor.shape()),
              errors::InvalidArgument("value_range should be a vector."));
  OP_REQUIRES(ctx, (value_range_tensor.shape().num_elements() == 2),
              errors::InvalidArgument(
                  "value_range should be a vector of 2 elements."));
  OP_REQUIRES(ctx, TensorShapeUtils::IsScalar(nbins_tensor.shape()),
              errors::InvalidArgument("nbins should be a scalar."));
  const auto values = values_tensor.flat<T>();
  const auto value_range = value_range_tensor.flat<T>();
  const auto nbins = nbins_tensor.scalar<int32>()();
  OP_REQUIRES(
      ctx, (value_range(0) < value_range(1)),
      errors::InvalidArgument("value_range should satisfy value_range[0] < "
                              "value_range[1], but got '[",
                              value_range(0), ", ", value_range(1), "]'"));
  OP_REQUIRES(
      ctx, (nbins > 0),
      errors::InvalidArgument("nbins should be a positive number, but got '",
                              nbins, "'"));
  Tensor* out_tensor;
  OP_REQUIRES_OK(ctx,
                 ctx->allocate_output(0, TensorShape({nbins}), &out_tensor));
  auto out = out_tensor->flat<Tout>();
  OP_REQUIRES_OK(
      ctx, functor::HistogramFixedWidthFunctor<Device, T, Tout>::Compute(
               ctx, values, value_range, nbins, out));
}

static int _process_tokenize(mcp_parser_t *pr, const size_t max) {
    const char *s = pr->request;
    int len = pr->reqlen - 2;
    if (len > PARSER_MAXLEN) {
        len = PARSER_MAXLEN;
    }
    const char *end = s + len;
    int curtoken = 0;
    int state = 0;
    while (s != end) {
        switch (state) {
            case 0:
                if (*s != ' ') {
                    pr->tokens[curtoken] = s - pr->request;
                    if (++curtoken == max) {
                        s++;
                        state = 2;
                        break;
                    }
                    state = 1;
                }
                s++;
                break;
            case 1:
                if (*s != ' ') {
                    s++;
                } else {
                    state = 0;
                }
                break;
            case 2:
                if (*s == ' ') {
                    goto endloop;
                }
                s++;
                break;
        }
    }
endloop:
    pr->tokens[curtoken] = s - pr->request;
    pr->ntokens = curtoken;
    P_DEBUG("%s: cur_tokens: %d\n", __func__, curtoken);
    return 0;
}

BOOL rdp_read_share_control_header(wStream* s, UINT16* length, UINT16* type, UINT16* channel_id)
{
	if (Stream_GetRemainingLength(s) < 2)
		return FALSE;
	Stream_Read_UINT16(s, *length); 
	if (*length == 0x8000)
	{
		rdp_read_flow_control_pdu(s, type);
		*channel_id = 0;
		*length = 8; 
		return TRUE;
	}
	if (((size_t)*length - 2) > Stream_GetRemainingLength(s))
		return FALSE;
	Stream_Read_UINT16(s, *type); 
	*type &= 0x0F;                
	if (*length > 4)
		Stream_Read_UINT16(s, *channel_id); 
	else
		*channel_id = 0; 
	return TRUE;
}

static int32_t check_load_area(uintptr_t dst, uintptr_t len)
{
	uint32_t legacy = dst + len <= UINT32_MAX - 1 ? 1 : 0;
	uintptr_t dram_start, dram_end;
	uintptr_t prot_start, prot_end;
	int32_t result = IO_SUCCESS;
	dram_start = legacy ? DRAM1_BASE : DRAM_40BIT_BASE;
	dram_end = legacy ? DRAM1_BASE + DRAM1_SIZE :
	    DRAM_40BIT_BASE + DRAM_40BIT_SIZE;
	prot_start = legacy ? DRAM_PROTECTED_BASE : DRAM_40BIT_PROTECTED_BASE;
	prot_end = prot_start + DRAM_PROTECTED_SIZE;
	if (dst < dram_start || dst > dram_end - len) {
		ERROR("BL2: dst address is on the protected area.\n");
		result = IO_FAIL;
		goto done;
	}
	if (dst >= prot_start && dst < prot_end) {
		ERROR("BL2: dst address is on the protected area.\n");
		result = IO_FAIL;
	}
	if (dst < prot_start && dst > prot_start - len) {
		ERROR("BL2: loaded data is on the protected area.\n");
		result = IO_FAIL;
	}
done:
	if (result == IO_FAIL) {
		ERROR("BL2: Out of range : dst=0x%lx len=0x%lx\n", dst, len);
	}
	return result;
}

