    oe_sgx_td_t* td,
    uint16_t func,
    uint16_t result,
    uint64_t arg)
{
    oe_callsite_t* callsite = td->callsites;
    if (!callsite)
        return;
    td->oret_func = func;
    td->oret_result = result;
    td->oret_arg = arg;
    asm volatile("pushq %[rflags] \n\t" 
                 "popfq \n\t"
                 "fldcw %[fcw] \n\t"     
                 "ldmxcsr %[mxcsr] \n\t" 
                 : [mxcsr] "=m"(callsite->mxcsr),
                   [fcw] "=m"(callsite->fcw),
                   [rflags] "=m"(callsite->rflags)
                 :
                 : "cc");
    oe_longjmp(&callsite->jmpbuf, 1);
}

