http_dissect_hdrs(struct worker *w, struct http *hp, int fd, char *p,
    const struct http_conn *htc)
{
	char *q, *r;
	txt t = htc->rxbuf;
	if (*p == '\r')
		p++;
	hp->nhd = HTTP_HDR_FIRST;
	hp->conds = 0;
	r = NULL;		
	for (; p < t.e; p = r) {
		q = r = p;
		while (r < t.e) {
			if (!vct_iscrlf(*r)) {
				r++;
				continue;
			}
			q = r;
			assert(r < t.e);
			r += vct_skipcrlf(r);
			if (r >= t.e)
				break;
			if (!vct_issp(*r))
				break;
			while (vct_islws(*q))
				*q++ = ' ';
		}
		if (q - p > htc->maxhdr) {
			VSC_C_main->losthdr++;
			WSL(w, SLT_LostHeader, fd, "%.*s",
			    q - p > 20 ? 20 : q - p, p);
			return (413);
		}
		if (p == q)
			break;
		if ((p[0] == 'i' || p[0] == 'I') &&
		    (p[1] == 'f' || p[1] == 'F') &&
		    p[2] == '-')
			hp->conds = 1;
		while (q > p && vct_issp(q[-1]))
			q--;
		*q = '\0';
		if (hp->nhd < hp->shd) {
			hp->hdf[hp->nhd] = 0;
			hp->hd[hp->nhd].b = p;
			hp->hd[hp->nhd].e = q;
			WSLH(w, fd, hp, hp->nhd);
			hp->nhd++;
		} else {
			VSC_C_main->losthdr++;
			WSL(w, SLT_LostHeader, fd, "%.*s",
			    q - p > 20 ? 20 : q - p, p);
			return (413);
		}
	}
	return (0);
}

format_fractional_part_nsecs(gchar *buf, size_t buflen, guint32 nsecs, const char *decimal_point, int precision)
{
	gsize decimal_point_len;
	gint8 num_buf[CHARS_NANOSECONDS];
	gint8 *num_end = &num_buf[CHARS_NANOSECONDS];
	gint8 *num_ptr;
	size_t num_len;
	ws_assert(precision != 0);
	if (buflen == 0) {
		return;
	}
	decimal_point_len = g_strlcpy(buf, decimal_point, buflen);
	if (decimal_point_len >= buflen) {
		return;
	}
	buf += decimal_point_len;
	buflen -= decimal_point_len;
	switch (precision) {
	case 1:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 100000000, precision);
		break;
	case 2:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 10000000, precision);
		break;
	case 3:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 1000000, precision);
		break;
	case 4:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 100000, precision);
		break;
	case 5:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 10000, precision);
		break;
	case 6:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 1000, precision);
		break;
	case 7:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 100, precision);
		break;
	case 8:
		num_ptr = uint_to_str_back_len(num_end,
		    nsecs / 10, precision);
		break;
	case 9:
		num_ptr = uint_to_str_back_len(num_end, nsecs,
		    precision);
		break;
	default:
		ws_assert_not_reached();
		break;
	}
	num_len = MIN((size_t)(num_end - num_ptr), buflen - 1);
	if (num_len == 0) {
		return;
	}
	memcpy(buf, num_ptr, num_len);
	*(buf + num_len) = '\0';
}

static bool vtable_is_addr_vtable_start_msvc(RVTableContext *context, ut64 curAddress) {
	RAnalRef *xref;
	RListIter *xrefIter;
	if (!curAddress || curAddress == UT64_MAX) {
		return false;
	}
	if (curAddress && !vtable_is_value_in_text_section (context, curAddress, NULL)) {
		return false;
	}
	RList *xrefs = r_anal_xrefs_get (context->anal, curAddress);
	if (r_list_empty (xrefs)) {
		r_list_free (xrefs);
		return false;
	}
	r_list_foreach (xrefs, xrefIter, xref) {
		if (vtable_addr_in_text_section (context, xref->addr)) {
			ut8 buf[VTABLE_BUFF_SIZE];
			context->anal->iob.read_at (context->anal->iob.io, xref->addr, buf, sizeof(buf));
			RAnalOp analop = {0};
			r_anal_op (context->anal, &analop, xref->addr, buf, sizeof(buf), R_ANAL_OP_MASK_BASIC);
			if (analop.type == R_ANAL_OP_TYPE_MOV
				|| analop.type == R_ANAL_OP_TYPE_LEA) {
				r_list_free (xrefs);
				r_anal_op_fini (&analop);
				return true;
			}
			r_anal_op_fini (&analop);
		}
	}
	r_list_free (xrefs);
	return false;
}

