DSA_Signature_Operation::raw_sign(const uint8_t msg[], size_t msg_len,
                                  RandomNumberGenerator& rng)
   {
   const BigInt& q = m_group.get_q();
   BigInt i(msg, msg_len, q.bits());
   while(i >= q)
      i -= q;
#if defined(BOTAN_HAS_RFC6979_GENERATOR)
   BOTAN_UNUSED(rng);
   const BigInt k = generate_rfc6979_nonce(m_x, q, i, m_rfc6979_hash);
#else
   const BigInt k = BigInt::random_integer(rng, 1, q);
#endif
   BigInt s = inverse_mod(k, q);
   const BigInt r = m_mod_q.reduce(m_group.power_g_p(k));
   s = m_mod_q.multiply(s, mul_add(m_x, r, i));
   if(r.is_zero() || s.is_zero())
      throw Internal_Error("Computed zero r/s during DSA signature");
   return BigInt::encode_fixed_length_int_pair(r, s, q.bytes());
   }

ngx_http_send_error_page(ngx_http_request_t *r, ngx_http_err_page_t *err_page)
{
    ngx_int_t                  overwrite;
    ngx_str_t                  uri, args;
    ngx_table_elt_t           *location;
    ngx_http_core_loc_conf_t  *clcf;
    overwrite = err_page->overwrite;
    if (overwrite && overwrite != NGX_HTTP_OK) {
        r->expect_tested = 1;
    }
    if (overwrite >= 0) {
        r->err_status = overwrite;
    }
    if (ngx_http_complex_value(r, &err_page->value, &uri) != NGX_OK) {
        return NGX_ERROR;
    }
    if (uri.len && uri.data[0] == '/') {
        if (err_page->value.lengths) {
            ngx_http_split_args(r, &uri, &args);
        } else {
            args = err_page->args;
        }
        if (r->method != NGX_HTTP_HEAD) {
            r->method = NGX_HTTP_GET;
            r->method_name = ngx_http_core_get_method;
        }
        return ngx_http_internal_redirect(r, &uri, &args);
    }
    if (uri.len && uri.data[0] == '@') {
        return ngx_http_named_location(r, &uri);
    }
    location = ngx_list_push(&r->headers_out.headers);
    if (location == NULL) {
        return NGX_ERROR;
    }
    if (overwrite != NGX_HTTP_MOVED_PERMANENTLY
        && overwrite != NGX_HTTP_MOVED_TEMPORARILY
        && overwrite != NGX_HTTP_SEE_OTHER
        && overwrite != NGX_HTTP_TEMPORARY_REDIRECT
        && overwrite != NGX_HTTP_PERMANENT_REDIRECT)
    {
        r->err_status = NGX_HTTP_MOVED_TEMPORARILY;
    }
    location->hash = 1;
    ngx_str_set(&location->key, "Location");
    location->value = uri;
    ngx_http_clear_location(r);
    r->headers_out.location = location;
    clcf = ngx_http_get_module_loc_conf(r, ngx_http_core_module);
    if (clcf->msie_refresh && r->headers_in.msie) {
        return ngx_http_send_refresh(r);
    }
    return ngx_http_send_special_response(r, clcf, r->err_status
                                                   - NGX_HTTP_MOVED_PERMANENTLY
                                                   + NGX_HTTP_OFF_3XX);
}

char *QuotedString::extractFrom(char *input, char **endPtr) {
  char firstChar = *input;
  if (!isQuote(firstChar)) {
    return NULL;
  }
  char stopChar = firstChar;  
  char *startPtr = input + 1;  
  char *readPtr = startPtr;
  char *writePtr = startPtr;
  char c;
  for (;;) {
    c = *readPtr++;
    if (c == '\0') {
      return NULL;
    }
    if (c == stopChar) {
      break;
    }
    if (c == '\\') {
      c = unescapeChar(*readPtr++);
    }
    *writePtr++ = c;
  }
  *writePtr = '\0';
  *endPtr = readPtr;
  return startPtr;
}

