int sub__add(struct mosquitto *context, const char *sub, uint8_t qos, uint32_t identifier, int options)
{
	int rc = 0;
	struct mosquitto__subhier *subhier;
	const char *sharename = NULL;
	char *local_sub;
	char **topics;
	size_t topiclen;
	assert(sub);
	rc = sub__topic_tokenise(sub, &local_sub, &topics, &sharename);
	if(rc) return rc;
	topiclen = strlen(topics[0]);
	if(topiclen > UINT16_MAX){
		mosquitto__free(local_sub);
		mosquitto__free(topics);
		return MOSQ_ERR_INVAL;
	}
	HASH_FIND(hh, db.subs, topics[0], topiclen, subhier);
	if(!subhier){
		subhier = sub__add_hier_entry(NULL, &db.subs, topics[0], (uint16_t)topiclen);
		if(!subhier){
			mosquitto__free(local_sub);
			mosquitto__free(topics);
			log__printf(NULL, MOSQ_LOG_ERR, "Error: Out of memory.");
			return MOSQ_ERR_NOMEM;
		}
	}
	rc = sub__add_context(context, sub, qos, identifier, options, subhier, topics, sharename);
	mosquitto__free(local_sub);
	mosquitto__free(topics);
	return rc;
}

juniper_atm2_print(netdissect_options *ndo,
                   const struct pcap_pkthdr *h, register const u_char *p)
{
        int llc_hdrlen;
        struct juniper_l2info_t l2info;
        l2info.pictype = DLT_JUNIPER_ATM2;
        if (juniper_parse_header(ndo, p, h, &l2info) == 0)
            return l2info.header_len;
        p+=l2info.header_len;
        if (l2info.cookie[7] & ATM2_PKT_TYPE_MASK) { 
            oam_print(ndo, p, l2info.length, ATM_OAM_NOHEC);
            return l2info.header_len;
        }
        if (EXTRACT_24BITS(p) == 0xfefe03 || 
            EXTRACT_24BITS(p) == 0xaaaa03) { 
            llc_hdrlen = llc_print(ndo, p, l2info.length, l2info.caplen, NULL, NULL);
            if (llc_hdrlen > 0)
                return l2info.header_len;
        }
        if (l2info.direction != JUNIPER_BPF_PKT_IN && 
            (EXTRACT_32BITS(l2info.cookie) & ATM2_GAP_COUNT_MASK)) {
            ether_print(ndo, p, l2info.length, l2info.caplen, NULL, NULL);
            return l2info.header_len;
        }
        if (p[0] == 0x03) { 
            isoclns_print(ndo, p + 1, l2info.length - 1, l2info.caplen - 1);
            return l2info.header_len;
        }
        if(juniper_ppp_heuristic_guess(ndo, p, l2info.length) != 0) 
            return l2info.header_len;
        if (ip_heuristic_guess(ndo, p, l2info.length) != 0) 
            return l2info.header_len;
	return l2info.header_len;
}

static ssize_t qrtr_tun_write_iter(struct kiocb *iocb, struct iov_iter *from)
{
	struct file *filp = iocb->ki_filp;
	struct qrtr_tun *tun = filp->private_data;
	size_t len = iov_iter_count(from);
	ssize_t ret;
	void *kbuf;
	kbuf = kzalloc(len, GFP_KERNEL);
