void CWebServer::GetFloorplanImage(WebEmSession & session, const request& req, reply & rep)
{
	std::string idx = request::findValue(&req, "idx");
	if (idx == "") {
		return;
	}
	std::vector<std::vector<std::string> > result;
	result = m_sql.safe_queryBlob("SELECT Image FROM Floorplans WHERE ID=%s", idx.c_str());
	if (result.empty())
		return;
	reply::set_content(&rep, result[0][0].begin(), result[0][0].end());
	std::string oname = "floorplan";
	if (result[0][0].size() > 10)
	{
		if (result[0][0][0] == 'P')
			oname += ".png";
		else if (result[0][0][0] == -1)
			oname += ".jpg";
		else if (result[0][0][0] == 'B')
			oname += ".bmp";
		else if (result[0][0][0] == 'G')
			oname += ".gif";
	}
	reply::add_header_attachment(&rep, oname);
}

Jsi_RC jsi_evalcode(jsi_Pstate *ps, Jsi_Func *func, Jsi_OpCodes *opcodes, 
         jsi_ScopeChain *scope, Jsi_Value *fargs,
         Jsi_Value *_this,
         Jsi_Value **vret)
{
    Jsi_Interp *interp = ps->interp;
    if (interp->exited)
        return JSI_ERROR;
    Jsi_RC rc;
    jsi_Frame frame = *interp->framePtr;
    frame.parent = interp->framePtr;
    interp->framePtr = &frame;
    frame.parent->child = interp->framePtr = &frame;
    frame.ps = ps;
    frame.ingsc = scope;
    frame.incsc = fargs;
    frame.inthis = _this;
    frame.opcodes = opcodes;
    frame.fileName = ((func && func->script)?func->script:interp->curFile);
    frame.funcName = interp->curFunction;
    frame.dirName = interp->curDir;
    if (frame.fileName && frame.fileName == frame.parent->fileName)
        frame.logflag = frame.parent->logflag;
    else
        frame.logflag = 0;
    frame.level = frame.parent->level+1;
    frame.evalFuncPtr = func;
    frame.arguments = NULL;
    if (interp->curIp)
        frame.parent->line = interp->curIp->Line;
    frame.ip = interp->curIp;
    interp->refCount++;
    interp->level++;
    Jsi_IncrRefCount(interp, fargs);
    rc = jsi_evalcode_sub(ps, opcodes, scope, fargs, _this, *vret);
    Jsi_DecrRefCount(interp, fargs);
    if (interp->didReturn == 0 && !interp->exited) {
        if ((interp->evalFlags&JSI_EVAL_RETURN)==0)
            Jsi_ValueMakeUndef(interp, vret);
    }
    if (frame.arguments)
        Jsi_DecrRefCount(interp, frame.arguments);
    interp->didReturn = 0;
    interp->refCount--;
    interp->level--;
    interp->framePtr = frame.parent;
    interp->framePtr->child = NULL;
    interp->curIp = frame.ip;
    if (interp->exited)
        rc = JSI_ERROR;
    return rc;
}

static void mem_cgroup_usage_unregister_event(struct cgroup *cgrp,
	struct cftype *cft, struct eventfd_ctx *eventfd)
{
	struct mem_cgroup *memcg = mem_cgroup_from_cont(cgrp);
	struct mem_cgroup_thresholds *thresholds;
	struct mem_cgroup_threshold_ary *new;
	int type = MEMFILE_TYPE(cft->private);
	u64 usage;
	int i, j, size;
	mutex_lock(&memcg->thresholds_lock);
	if (type == _MEM)
		thresholds = &memcg->thresholds;
	else if (type == _MEMSWAP)
		thresholds = &memcg->memsw_thresholds;
	else
		BUG();
 	BUG_ON(!thresholds);
 	usage = mem_cgroup_usage(memcg, type == _MEMSWAP);
	__mem_cgroup_threshold(memcg, type == _MEMSWAP);
	size = 0;
	for (i = 0; i < thresholds->primary->size; i++) {
		if (thresholds->primary->entries[i].eventfd != eventfd)
			size++;
	}
	new = thresholds->spare;
	if (!size) {
		kfree(new);
		new = NULL;
		goto swap_buffers;
	}
	new->size = size;
	new->current_threshold = -1;
	for (i = 0, j = 0; i < thresholds->primary->size; i++) {
		if (thresholds->primary->entries[i].eventfd == eventfd)
			continue;
		new->entries[j] = thresholds->primary->entries[i];
		if (new->entries[j].threshold < usage) {
			++new->current_threshold;
		}
		j++;
	}
swap_buffers:
	thresholds->spare = thresholds->primary;
	rcu_assign_pointer(thresholds->primary, new);
 	synchronize_rcu();
 	mutex_unlock(&memcg->thresholds_lock);
 }

static void bnx2x_recovery_failed(struct bnx2x *bp)
{
	netdev_err(bp->dev, "Recovery has failed. Power cycle is needed.\n");
	netif_device_detach(bp->dev);
	bnx2x_set_reset_in_progress(bp);
	bnx2x_set_power_state(bp, PCI_D3hot);
