static void set_banner(struct openconnect_info *vpninfo)
{
        char *banner, *q;
        const char *p;
       if (!vpninfo->banner || !(banner = malloc(strlen(vpninfo->banner)))) {
                unsetenv("CISCO_BANNER");
                return;
        }
	p = vpninfo->banner;
	q = banner;
	while (*p) {
		if (*p == '%' && isxdigit((int)(unsigned char)p[1]) &&
		    isxdigit((int)(unsigned char)p[2])) {
			*(q++) = unhex(p + 1);
			p += 3;
		} else 
			*(q++) = *(p++);
	}
	*q = 0;
	setenv("CISCO_BANNER", banner, 1);
	free(banner);
}

int rtp_packetize_xiph_config( sout_stream_id_sys_t *id, const char *fmtp,
                               int64_t i_pts )
{
    if (fmtp == NULL)
        return VLC_EGENERIC;
    char *start = strstr(fmtp, "configuration=");
    assert(start != NULL);
    start += sizeof("configuration=") - 1;
    char *end = strchr(start, ';');
    assert(end != NULL);
    size_t len = end - start;
    char b64[len + 1];
    memcpy(b64, start, len);
    b64[len] = '\0';
    int     i_max   = rtp_mtu (id) - 6; 
    uint8_t *p_orig, *p_data;
    int i_data;
    i_data = vlc_b64_decode_binary(&p_orig, b64);
    if (i_data <= 9)
    {
        free(p_orig);
        return VLC_EGENERIC;
    }
    p_data = p_orig + 9;
    i_data -= 9;
    int i_count = ( i_data + i_max - 1 ) / i_max;
    for( int i = 0; i < i_count; i++ )
    {
        int           i_payload = __MIN( i_max, i_data );
        block_t *out = block_Alloc( 18 + i_payload );
        unsigned fragtype, numpkts;
        if (i_count == 1)
        {
            fragtype = 0;
            numpkts = 1;
        }
        else
        {
            numpkts = 0;
            if (i == 0)
                fragtype = 1;
            else if (i == i_count - 1)
                fragtype = 3;
            else
                fragtype = 2;
        }
        uint32_t header = ((XIPH_IDENT & 0xffffff) << 8) |
                          (fragtype << 6) | (1 << 4) | numpkts;
        rtp_packetize_common( id, out, 0, i_pts );
        SetDWBE( out->p_buffer + 12, header);
        SetWBE( out->p_buffer + 16, i_payload);
        memcpy( &out->p_buffer[18], p_data, i_payload );
        out->i_dts    = i_pts;
        rtp_packetize_send( id, out );
        p_data += i_payload;
        i_data -= i_payload;
    }
    free(p_orig);
    return VLC_SUCCESS;
}

Status FillCollectiveParams(CollectiveParams* col_params,
                            CollectiveType collective_type,
                            const Tensor& group_size, const Tensor& group_key,
                            const Tensor& instance_key) {
  if (group_size.dims() > 0) {
    return errors::Internal("Unexpected dimensions on input group_size, got ",
                            group_size.shape().DebugString());
  }
  if (group_key.dims() > 0) {
    return errors::Internal("Unexpected dimensions on input group_key, got ",
                            group_key.shape().DebugString());
  }
  if (instance_key.dims() > 0) {
    return errors::Internal(
        "Unexpected dimensions on input instance_key, got ",
        instance_key.shape().DebugString());
  }
  col_params->name = name_;
  col_params->group.device_type = device_type_;
  col_params->group.group_size = group_size.unaligned_flat<int32>()(0);
  if (col_params->group.group_size <= 0) {
    return errors::InvalidArgument(
        "group_size must be positive integer but got ",
        col_params->group.group_size);
  }
  col_params->group.group_key = group_key.unaligned_flat<int32>()(0);
  col_params->instance.type = collective_type;
  col_params->instance.instance_key = instance_key.unaligned_flat<int32>()(0);
  col_params->instance.data_type = data_type_;
  col_params->instance.impl_details.communication_hint = communication_hint_;
  col_params->instance.impl_details.timeout_seconds = timeout_seconds_;
  return Status::OK();
}

