pci_vtrnd_notify(void *vsc, struct vqueue_info *vq)
{
	struct iovec iov;
	struct pci_vtrnd_softc *sc;
	int len;
	uint16_t idx;
	sc = vsc;
	if (sc->vrsc_fd < 0) {
		vq_endchains(vq, 0);
		return;
	}
	while (vq_has_descs(vq)) {
		vq_getchain(vq, &idx, &iov, 1, NULL);
		len = (int) read(sc->vrsc_fd, iov.iov_base, iov.iov_len);
		DPRINTF(("vtrnd: vtrnd_notify(): %d\r\n", len));
		assert(len > 0);
		vq_relchain(vq, idx, (uint32_t)len);
	}
	vq_endchains(vq, 1);	
}

int32_t PPB_Flash_MessageLoop_Impl::InternalRun(
    const RunFromHostProxyCallback& callback) {
  if (state_->run_called()) {
    if (!callback.is_null())
      callback.Run(PP_ERROR_FAILED);
    return PP_ERROR_FAILED;
  }
  state_->set_run_called();
  state_->set_run_callback(callback);
  scoped_refptr<State> state_protector(state_);
   {
     base::MessageLoop::ScopedNestableTaskAllower allow(
         base::MessageLoop::current());
     base::MessageLoop::current()->Run();
   }
  return state_protector->result();
}

rleUncompress (int inLength, int maxLength, const signed char in[], char out[])
{
    char *outStart = out;
    while (inLength > 0)
    {
	if (*in < 0)
	{
	    int count = -((int)*in++);
	    inLength -= count + 1;
	    if (0 > (maxLength -= count))
		return 0;
        if (inLength < 0)
          return 0;
        memcpy(out, in, count);
        out += count;
        in  += count;
	}
	else
	{
	    int count = *in++;
	    inLength -= 2;
	    if (0 > (maxLength -= count + 1))
		return 0;
        memset(out, *(char*)in, count+1);
        out += count+1;
	    in++;
	}
    }
    return out - outStart;
}

