	record = newShapeRecord(shape, SHAPERECORD_LINETO);
	SWF_assert(SWFOutput_numSBits(dx) < 18);
	SWF_assert(SWFOutput_numSBits(dy) < 18);
	record.record.lineTo->dx = dx;
	record.record.lineTo->dy = dy;
	shape->xpos += dx;
